/*Table structure for table `code_list` */

DROP TABLE IF EXISTS `code_list`;

CREATE TABLE `code_list` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(200) NOT NULL,
  `typeId` int(11) DEFAULT NULL,
  `price` float DEFAULT '0',
  `adminId` int(11) DEFAULT NULL,
  `userId` int(11) DEFAULT NULL,
  `origin` varchar(10) DEFAULT '0',
  `update` datetime DEFAULT NULL,
  `keywords` varchar(200) DEFAULT NULL,
  `summary` varchar(500) DEFAULT NULL,
  `content` text,
  `sort` int(11) DEFAULT '0',
  `status` int(11) DEFAULT '0',
  `acs` int(11) DEFAULT '0',
  `lookCount` int(11) DEFAULT '0',
  `mentCount` int(11) DEFAULT '0',
  `downCount` int(11) DEFAULT '0',
  `dpCount` int(11) DEFAULT '0',
  `downUrl` varchar(200) DEFAULT NULL,
  `extPwd` varchar(20) DEFAULT NULL,
  `decomPwd` varchar(20) DEFAULT NULL,
  `tbPath` varchar(200) DEFAULT NULL,
  `filePath` varchar(200) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `FK_code_list` (`typeId`),
  KEY `FK_code_list2` (`adminId`),
  KEY `FK_code_list3` (`userId`),
  CONSTRAINT `FK_code_list` FOREIGN KEY (`typeId`) REFERENCES `code_type` (`id`),
  CONSTRAINT `FK_code_list2` FOREIGN KEY (`adminId`) REFERENCES `admin` (`id`),
  CONSTRAINT `FK_code_list3` FOREIGN KEY (`userId`) REFERENCES `user_list` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=23 DEFAULT CHARSET=utf8;

/*Data for the table `code_list` */

insert  into `code_list`(`id`,`title`,`typeId`,`price`,`adminId`,`userId`,`origin`,`update`,`keywords`,`summary`,`content`,`sort`,`status`,`acs`,`lookCount`,`mentCount`,`downCount`,`dpCount`,`downUrl`,`extPwd`,`decomPwd`,`tbPath`,`filePath`) values (1,'特斯拉的 Model 3 效应——利好与风险共存',1,100,1,NULL,'原创','2017-12-12 00:22:40','特斯拉的 Model 3 效应——利好与风险共存','特斯拉的 Model 3 效应——利好与风险共存','<p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/jsp/upload/image/20171212/1513009408030091798.jpg\" width=\"100%\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">文 | 于欣烈</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">昨天的 Model 3 交付仪式后，越来越多的人把特斯拉比作苹果，并认为 Model 3 堪比苹果标志性产物 iPhone 4。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">从昨天的交付仪式来看，至少有一点，特斯拉与苹果有明显的不同——公布会。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">特斯拉的发布会，更像是一场 Elon Musk 的小我私家聚会，或者说，属于 Elon Musk 的表演时候。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">乔布斯辞世后的苹果发布会，本质上已经变成一家大型公司举办的有等级、有秩序、有仪式感的发布会。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">参加特斯拉发布会的人，更像是听了 Elon Musk 的一场脱口秀。而对 Elon Musk 的崇拜，正是很多人购买特斯拉电动车的重要原因。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/jsp/upload/image/20171212/1513009408171035283.png\" width=\"100%\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">这场个人演出在特斯拉的 Fremont 工厂里举行。黑色的地板、灰色的背景墙打出的「Model 3 」十分醒目。整个会场灯光昏暗，呈现出一种剧场的氛围。在聚光灯中，Elon Musk 本身开着一辆红色的 Model 3 驶上舞台。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/jsp/upload/image/20171212/1513009408233068129.png\" width=\"100%\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">下车后，Elon Musk 高举双手向台前情绪激动的上千名媒体和内部员工致意。而这辆红的 Model 3 被明亮的聚光灯笼罩，并面对台下观众旋转到最佳角度。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">站在舞台跟前的观众们，不时对 Elon 的演讲内容发出阵阵惊叹、欢呼或大笑。这是属于他们和 Elon Musk 的时刻。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/jsp/upload/image/20171212/1513009408296017427.png\" width=\"100%\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/jsp/upload/image/20171212/1513009408358073166.png\" width=\"100%\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><strong style=\"margin: 0px; padding: 0px;\">转折点</strong></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">Model 3 对特斯拉来说，的确很像 iPhone 4 之于苹果——一款销量暴涨的产品，更是一家公司发展的关键转折点。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">这是特斯拉第一款面向大众市场的电动车，起售价3.5万美元（约合人民币）。在 Model 3 之前，最便宜的特斯拉电动车也要近7万美元，2016年特斯拉年销量仅8.4万辆。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">而Model 3 上市后，Elon Musk 为特斯拉设定的销量目的是2018年50万辆，2020年到达100万辆。Model 3 对于特斯拉的意义不言而喻。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/jsp/upload/image/20171212/1513009408421093775.png\" width=\"100%\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">如此巨大的销量不仅仅意味着更多的电动车市场份额，还能在2年多的时间内给特斯拉带来上百万的 Autopilo 用户和上百万辆天天在道路上收集主动驾驶技术数据的终端。这会帮助特斯拉在自动驾驶技术上实现快速的成长。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">Elon Musk 在发布会上如许评价 Model 3：「今天是特斯拉的大日子，Model 3 是款巨大且代价亲民的电动车。世界上有伟大的汽油车，有伟大的豪华车，但是没有过伟大且价格亲民的电动车。」</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">但硬币都有两面。Model 3 的上市，对特斯拉来说也会有负面影响。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">之前乐于花近百万元人民币购买 Model S 的用户，大多数看中的都是特斯拉电动车极致的加快性和 Autopilot 自动驾驶帮助功能。讲真，现在 S/X 的工艺质量还达不到百万元豪车的尺度。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">而这两者，Model 3 都能提供。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">零百公里加速参数上，Model 3 会比 Model S 慢零点几秒，但对绝大多数用户而言，体验不出太大的差别。凭据这次 Model 3 交付仪式前的试驾反馈，有很多媒体都对 Model 3 的加速性大加称赞。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">而 Model 3 上配备了和 Model S 相同的 Autopilot 硬件配置，只需选购了 Autopilot 服务就能启用这个高级自动驾驶辅助系统。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">从这两方面来看，Model 3 就是一款价格更便宜、空间更小的 Model S。那购买者为什么要花7万美元而不是3.5万美元买一辆特斯拉电动车？</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/jsp/upload/image/20171212/1513009408483054951.png\" width=\"100%\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">目前特斯拉的整体销量中 Model S 占比更高，特别是在美国市场，据彭博社的数据，今年以来 Model S 基本月销量在7000辆左右，是销量中的主力车型。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">假如将来更多的订单被高性价比的 Model 3 抢走，则意味着 S/X 的销量会受到不小的影响。而受产能限制，Model 3 今年的产量会很有限，并且特斯拉为 Model 3 量产投入巨大。所以一旦 S/X 的销量无法实现增长目标，特斯拉今年下半年的财报会不太好看，那么特斯拉在资本市场会受到巨大影响。而特斯拉是一家非常缺钱的公司。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/jsp/upload/image/20171212/1513009408593072107.png\" width=\"100%\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><strong style=\"margin: 0px; padding: 0px;\">对「中国特斯拉们」的影响</strong></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">关于特斯拉入华，前不久闹得沸沸扬扬最终却无定论，但反映出特斯拉正在积极推进国产化历程。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">起首，国产特斯拉电动车售价会很有竞争力。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">如果未来 Model 3 能够国产，不仅工艺质量能有所提升，而且零售价格会大幅低落。你想想，一辆售价3.5万美元起（20多万元人民币）的特斯拉电动车，在中国市场畸形的电动车市场中一定会极具竞争力。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">即便不考虑补贴，这样的价格加上特斯拉的品牌效应，Model 3 的市场收割能力也是相当可怕的。（当然，特斯拉能否顺利在华建厂还取决于政府的态度）</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">其次，造一款性能对标特斯拉的电动车，本钱很难降低。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">好，既然你特斯拉国产 Model 3，那我可以搞一款「中国版 Model X」来打性价比吧？</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">特斯拉已经成功界说了什么是好电动车的标准，你要想达到这个标准，成本就会很高。你要想大幅降低成本，产品力就会没有特斯拉「好」。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">电动车的特性决定，要想实现出色的加速、续航里程，电池、电机和电控的成本在现阶段很难降低。特斯拉由于自己 Gigafactory 优势，成本比竞争敌手甚至更低。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/jsp/upload/image/20171212/1513009408671041406.png\" width=\"100%\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">特斯拉再时不时透露下即将国产 Model X 的消息，你还要花十几个月筹备车型的量产。你说消费者会怎么想？</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">对「中国特斯拉」们，我的建议是，放弃投机想法，别去根据特斯拉对电动车的定义去做电动车。按照别人的游戏规则去玩儿，你怎么能是对手？</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">现在已经有少数中国新兴造车公司并没有走捷径，而是根据中国用户对电动车的刚需和痛点，开发适合中国用户的电动车。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/jsp/upload/image/20171212/1513009408749061187.png\" width=\"100%\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/jsp/upload/image/20171212/1513009408811053247.png\" width=\"100%\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">比如中国主流用户完全不具备美国用户的充电桩条件，蔚来和车和家拿出的新充电方式电动车。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">毕竟特斯拉的车型研发都是在美国完成，针对的是美国用户的利用条件和使用习惯。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">或许这是明智的做法。</p><p><br/></p>',0,1,0,0,0,0,5,'http://','','','resource/images/thumb/1513008116014.jpg',''),(2,'天猫汽车自动贩卖机官方演示：蚂蚁分750分一成首付',2,200,1,NULL,'原创','2017-12-12 00:23:32','天猫汽车自动贩卖机官方演示：蚂蚁分750分一成首付','以往买一辆新车大概必要如许的流程：网上挑选、4S店看车、试乘试驾、和贩卖谈代价、交定金、验车提车...这样的模式连续了很多年，但天猫“汽车自动贩售机”的出现，将彻底颠覆传统汽车销售模式，让买车也能像买饮料一样简朴。本日，天猫官方微博放出了一段天猫“汽车自动贩售机”的演示视频，“天猫汽车自动贩售机”形状酷似立体车库，买车人可通过汽车自 ...','<p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">以往买一辆新车大概必要如许的流程：网上挑选、4S店看车、试乘试驾、和贩卖谈代价、交定金、验车提车...</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">这样的模式连续了很多年，但天猫“汽车自动贩售机”的出现，将彻底颠覆传统汽车销售模式，让买车也能像买饮料一样简朴。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img alt=\"天猫汽车自动贩卖机官方演示：蚂蚁分 750 分一成首付\" src=\"/ueditor/jsp/upload/image/20171212/1513009460171036146.jpg\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">本日，天猫官方微博放出了一段天猫“汽车自动贩售机”的演示视频，<strong style=\"margin: 0px; padding: 0px;\">“天猫汽车自动贩售机”形状酷似立体车库，买车人可通过汽车自动贩卖机提供的自助机验证身份，芝麻信用 750 分考核通过、一成首付、付出宝缴月供，简单三个步调就能实现线上买车。</strong></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">伴随着托盘的徐徐转动，提车口的灯亮了起来，一辆崭新的白色mini cooper就出如今面前。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">在 7 月 26 日启动的“天猫汽车嘉年华”上，<strong style=\"margin: 0px; padding: 0px;\">天猫汽车总经理俞巍公布，这种“汽车自动贩卖机”有望年内和消费者晤面</strong>。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">一起感觉一下：点此寓目</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img alt=\"天猫汽车自动贩卖机官方演示：蚂蚁分 750 分一成首付\" src=\"/ueditor/jsp/upload/image/20171212/1513009460311064729.jpg\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img alt=\"天猫汽车自动贩卖机官方演示：蚂蚁分 750 分一成首付\" src=\"/ueditor/jsp/upload/image/20171212/1513009460374041228.jpg\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img alt=\"天猫汽车自动贩卖机官方演示：蚂蚁分 750 分一成首付\" src=\"/ueditor/jsp/upload/image/20171212/1513009460436093522.jpg\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img alt=\"天猫汽车自动贩卖机官方演示：蚂蚁分 750 分一成首付\" src=\"/ueditor/jsp/upload/image/20171212/1513009460483070807.jpg\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img alt=\"天猫汽车自动贩卖机官方演示：蚂蚁分 750 分一成首付\" src=\"/ueditor/jsp/upload/image/20171212/1513009460546011701.jpg\"/></p><p><br/></p>',0,1,0,0,0,0,12,'http://','','','resource/images/thumb/1513009446733.jpg',''),(3,'百度Q2财报发布，李彦宏：搜索业务已逐渐摆脱既往负面影响',3,115,1,NULL,'原创','2017-12-12 00:26:26','百度Q2财报发布，李彦宏：搜索业务已逐渐摆脱既往负面影响','站长之家(Chinaz.com) 7 月 28 日消息今日，百度宣布了 2017 财年第二季度财报，报告显示百度第二季度总营收为人民币208. 74 亿元(约合30. 79 亿美元)，较客岁同期增长14.3%。据百度第二季度财报数据显示，百度营收为208. 74 亿人民币(约合30. 79 亿美元)，同比增长14.3%;净利润44. 15 亿人民币(约合6. 51 亿美元)，同比增长82.9%;非美国通用会计准则下， ...','<p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"/ueditor/jsp/upload/image/20171212/1513009634421025580.jpg\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><strong style=\"margin: 0px; padding: 0px;\">站长之家(Chinaz.com) 7 月 28 日消息&nbsp;&nbsp;&nbsp;</strong>&nbsp;今日，百度宣布了 2017 财年第二季度财报，报告显示百度第二季度总营收为人民币208. 74 亿元(约合30. 79 亿美元)，较客岁同期增长14.3%。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">据百度第二季度财报数据显示，百度营收为208. 74 亿人民币(约合30. 79 亿美元)，同比增长14.3%;净利润44. 15 亿人民币(约合6. 51 亿美元)，同比增长82.9%;非美国通用会计准则下，净利润55. 71 亿元人民币(约合8. 22 亿美元)，同比增长98.4%;其中移动营收占比72%，高于去年同期的62%。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">财报公布后，李彦宏发了一份内部信，信中称第二季度百度在多个行业增加了近 20000 名在线活泼客户，客户的连续认可和积极投放，说明我们的搜刮业务已逐渐摆脱既往负面影响，更是百度团体平台代价复苏的最有力证明!</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><strong style=\"margin: 0px; padding: 0px;\">&nbsp;以下为李彦宏内部信原文：</strong></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">各位亲爱的百度同学们：</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">各人好。北京时间 7 月 28 日，我们公布了百度 2017 年第二季度的财务报告。过去这个季度公司营业收入约为208. 74 亿元人民币，比去年同期增长14.3%。人工智能期间从“下一幕”走进“这一幕”的变革时候，闪耀着每一个百度人不懈创新、执着前行的光辉。在此，我谨代表公司董事会和管理层向大家表示由衷的感谢!</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">第二季度，在“夯实移动底子，决胜AI时代”的焦点战略之路上，我们的步伐坚定而坚实，移动收入占比达72%，AI正日益成为提拔公司核心业务与核心产物的基础驱动力。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">“搜索+信息流”的双重引擎不停加大马力，并依托AI的支撑，带给用户更流畅、更富厚的信息天下。信息流的日活跃用户(DAU)超过 1 亿，新推出的手百9. 0 版本使搜索体验越发形象化、语音化。百科、知道、贴吧迭代了全新的内容产品形态。 6 月份统计数据显示，爱奇艺用户每月花在PC和移动APP上的时间分别高达 1240 亿分钟和 3830 亿分钟，媒体时间占有本领仅次于微信。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">这些努力和成果，都在推动我们在智能时代做好内容分发和办事毗连，让百度真正成为最懂用户的公司，也让客户越来越接受这样的组合服务：<strong style=\"margin: 0px; padding: 0px;\">第二季度我们在教育、汽车、物流、本地服务等多个行业增加了近 20000 名在线活跃客户</strong>;相比较一季度，大客户原生广告收入提升250%，信息流广告收入提升200%。<strong style=\"margin: 0px; padding: 0px;\">客户的持续认可和积极投放，说明我们的搜索业务已逐渐摆脱既往负面影响，更是百度整体平台价值复苏的最有力证明!</strong></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">我们的人工智能的提前探索与互助布局，在第二季度迎来引爆财产的契机。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">七月初的AI开发者大会上，我们发布了Apollo和DuerOS两大开放平台，第一次向行业展示最完整、最开放、最前沿、也最具活力的AI生态。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">我们正式发布了Apollo 1. 0 和Apollo开放平台的整体战略与开放路线图，推出了国内首个可量产的车载主动驾驶盘算平台BCU(Baidu Computing Unit)，并和福特、戴姆勒、微软、博世、大陆、奇瑞、一汽等 50 家合作同伴(包括 13 家整车厂商)构建起环球最强大的自动驾驶生态，帮助伙伴实现“从 0 到1”的能力跨越，推动数据资源的交换与孝敬，加快创新诞生和技能迭代。无人驾驶乃至整个汽车产业的格局和将来，因为Apollo而变得不同以往!</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">DuerOS开放平台目前已具备 10 大类目、100+功能，为手机、智能家居、智能穿戴、车载等多个行业赋能。对人工智能创业公司KITT.AI的全资收购，将进一步增强我们的语音唤醒能力和自然语言理解技术深度。作为下一代基于自然语言的人类计算平台，DuerOS正在中国为全球行业树立新的标准。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">第二季度也是百度金融立足智能金融、培养核心能力的关键时期。上个月，我们与中国农业银行达成合作、共建智能银行，在客户的信用评估、风险管理和产品定位等丰富的应用场景中为传统金融贡献AI Fintech之力，以更加开放的心态与生态伙伴分享、共赢。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">基于人工智能的大数据云计算(ABC)已经成为百度整体人工智能战略行业输出的承担者，冷存储、快存储、存储网关等产品技术也居于业界领先位置。以此为基础，百度云正在开创不同重点行业的云化转型新路径，并构建百度云特色的整体行业生态。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">重视人工智能已渐成全球共识，如大河汤汤、不可逆转。这是所有百度人等待已久的全新机遇，让我们充满了信心和斗志，也对我们的事业提出了更高的要求。五四青年节那天，我们发布了公司的新使命，把原来的“让人们最平等便捷地获取信息找到所求”延伸为“用科技让复杂的世界更简单”。这也意味着百度已经成为一家AI公司，以新使命为感召，不断强化技术创新在在社会、政策层面的改变气力：上个月，我们与南航合作，在河南南阳机场首次实现刷脸登机;与重庆市政府、山西省政府等也签署了合作协议，共同加速智慧城市建设和产业转型升级……未来随着技术能力的不断完备、应用场景的不断丰富，从“连接信息”到“唤醒万物”的理想也必将更为清晰、触手可及。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">同学们，人工智能时代已经是“现在进行时”。我们的技术已经做好准备，用户与合作伙伴正充满期待，上个礼拜国务院印发《新一代人工智能生长规划》，为整个产业确立发展蓝图，也为百度AI技术在语音、图像、自动驾驶等更多领域的成熟应用与快速普及，创造了前所未有的政策环境。“千红万紫安排著，只待新雷第一声”，改变世界的蓝图正在我们眼前展开，我们唯有以AI时代的思维，坚持开放、共享的精神;唯有时刻把用户的利益放在心中，聚拢起更多的支持与力量，才能成为用户至上的创新者，才能真正谱写出代表中国时刻的AI乐章!</p><p><br/></p>',0,1,0,0,0,0,66,'http://','','','resource/images/thumb/1513009616546.jpg',''),(4,'从百度信息流日活破亿，谈威胁今日头条的到底是什么',1,200,1,NULL,'原创','2017-12-12 00:28:26','从百度信息流日活破亿，谈威胁今日头条的到底是什么','最近百度发布了Q2 季度财报，其净利润44. 15 亿人民币(约合6. 51 亿美元)，同比增长82.9%，移动营收占比72%，高于去年同期的62%。而这些数据也让资本更为看好百度的潜力，也让其在开盘后大涨 7 个点。本次财报除了强调人工智能之外，本次还重点谈到了内容分发，现在百度信息流日均活跃数已经到达1亿规模，成为继手机百度等产品之后，百度的又一个日活过亿 ...','<p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><strong style=\"margin: 0px; padding: 0px;\"><img src=\"/ueditor/jsp/upload/image/20171212/1513009754483096046.jpg\" alt=\"\"/></strong></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">最近百度发布了Q2 季度财报，其净利润44. 15 亿人民币(约合6. 51 亿美元)，同比增长82.9%，移动营收占比72%，高于去年同期的62%。而这些数据也让资本更为看好百度的潜力，也让其在开盘后大涨 7 个点。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">本次财报除了强调人工智能之外，本次还重点谈到了内容分发，现在百度信息流日均活跃数已经到达1亿规模，成为继手机百度等产品之后，百度的又一个日活过亿的重磅产品。本日头条达到这一数据用了整整5年时间，而百度信息流用时仅为一年，这也是百度收入增长的一支紧张力量。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">内容分发市场已成各家争夺的重点，除了百度，腾讯、阿里以及各大新闻客户端们，均在信息流范畴发力，并展示出了不小的实力，而在各家的合力之下，今日头条原本的市场份额也在受到一路的挤压，此次百度信息流过亿的日活，则是一个标志性信号，而借此我们可以再来看一下究竟什么才是头条的威胁。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><strong style=\"margin: 0px; padding: 0px;\">渠道分发的威胁</strong></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">内容分发市场目前固然仍旧处于混战阶段，但无论如何打下去，可以预判的是，这个行业的最终都不会出现一家独大的局面，而是依然保持数家共同分食的局面。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">原因在于信息流本质上并不是一个拥有很强壁垒的贸易模式，但凡拥有渠道上风的产品，就可以容易参加信息流模式，实现更多的额外的商业代价，只要渠道在，信息流市场就在。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">而总体来看重要有这几大敌手。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><strong style=\"margin: 0px; padding: 0px;\">1</strong><strong style=\"margin: 0px; padding: 0px;\">)东西类产品渠道，</strong>目前大量拥有千万级别、过亿级别的工具类产品都已经加入信息流产品，构建自身矩阵联盟。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">以百度为例，其为旗下月活过亿的百度舆图、百度网盘等明星产品接入了信息流，此外其还与WIFI万能钥匙、墨迹天气等国内顶工具类产品达成合作，实现信息流内容内容分发，构建起了强大的信息流分发体系。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><strong style=\"margin: 0px; padding: 0px;\">2</strong><strong style=\"margin: 0px; padding: 0px;\">)手机捆绑渠道，</strong>另一个分食信息流市场的则是手机渠道，《根据QM的《移动互联网 2017 年Q2 夏季报告》显示，一点资讯的月活数据超过了今日头条，达到 18093 万的月活，超过头条的 17826 万，统计中包含了一点资讯合作方小米与oppo的相关数据。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">这也说明拥有海量出货能力的手机厂商，也有能力在信息流分发市场上可以或许占据一席之地。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><strong style=\"margin: 0px; padding: 0px;\">3</strong><strong style=\"margin: 0px; padding: 0px;\">)搜刮渠道，</strong>百度信息流的增长，也是一支重要力量，但这又与其他渠道差别，百度还天然握有用户的搜索数据，当其他渠道类产品需要摸索前进时，百度可以在搜索数据的基础之上一步到位的为用户保举其感兴趣的内容，完成迭代。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">而此次百度信息流日活跃数能够破亿，也正式得益于搜索数据优势，而百度阶段性的成功，也引发了另一个搜索巨头的模仿，谷歌也与近期在其APP中加入了信息流推荐，效仿百度。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><strong style=\"margin: 0px; padding: 0px;\">内容平台的威胁</strong></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">头条目前正在不断孵化各种垂类，如火山直播、西瓜视频、悟空问答等等，这一方面是头条对付流量的精细化运营，而另一方面则是整个内容产业，迎来了自己的黄金期间，而头条也要进入这些市场分一杯羹。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">因此，值得关注的是那些本来在各个垂类内容领域拥有壁垒优势的对手。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><strong style=\"margin: 0px; padding: 0px;\">在直播领域，</strong>微博和陌陌已是最大赢家，其地位无可撼动，<strong style=\"margin: 0px; padding: 0px;\">短视频领域，</strong>头条虽然进击凶猛但是却面对着真实的版权问题，此前还遭遇到腾讯与搜狐的版权诉讼，而在其大力进军的问答领域，其主要流量依然来自于头条APP，而用户对于其独立产品尚无太大品牌感知，难以独立，而对面的知乎则早已进入知识付费阶段。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">因此，垂类内容平台由于有先发优势，并在垂类领域深耕多年，因此在内容时代，其价值也正在越来越大，对手无法轻易超越。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">因此，目前BAT也已都已经进入到内容生态阶段，各自都拥有好几个阶段的内容池，并将海量的内容分发到各个产品线中，如阿里会分发到UC浏览器、高德地图、支付宝每天有料等等，而腾讯则会分发到腾讯新闻、QQ看点、天天快报、微信看一看等等诸多产品中，而百度本身就是做搜索起家，因此在分发上更为注重。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">以百度为例，旗下拥有百度知道、百度百科、百度文库、百度贴吧等诸多内容产品，但与头条不同的是，十几年的深耕，百度旗下的这些产品也早已有了自己的品牌，并受到用户的认可。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">在内容生态时代，用户需要在不同场景下获取不同的内容，百度旗下这些积聚数年的产品线对于整个百度的内容生态来说，其既可以为整个百度内容生态提供内容池，同时又可以自成一体，自己完成内容的生产到分发全过程，而其价值在内容时代也正在变得越来越大，这也正是今日头条所渴望的。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><strong style=\"margin: 0px; padding: 0px;\">内容同质化的威胁</strong></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">目前头条已经抽走大量对于自媒体图文内容的补贴以及流量，将其投入到短视频当中，宣布要在 2017 年向短视频投入 10 亿补贴，可以看出通过自媒体图文内容获取用户的方式，已经进入到阶段性瓶颈阶段，短视频更像是其最后一搏。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">反观头条的对手们，其仍然在加大补贴力度，腾讯芒种2. 0 拿出 10 亿，阿里大鱼拿出 10 亿，网易号拿出 10 亿等等，而这些平台对于图文类自媒体、短视频类自媒体一起扶持，毫不手软，这样一来的结果很明显，各个平台都能够吸引优质的自媒体入住，各平台的内容都趋于雷同，头条不具备独家优势。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">但国民总时间又是固定的，大量渠道类产品分发的内容趋于相同，用户的满意度趋于相同，也越来越不会挑剔通过某一款客户端来满足自己的信息流阅读需求，头条早期的先发优势也将越来越弱化。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">因此，在这样互相挤压的格局之下，头条弱还要想保持高速增长，则必须考虑在未来打造出独家优质内容的壁垒，让用户有非用不可的理由。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><strong style=\"margin: 0px; padding: 0px;\">结语：</strong></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">百度信息流破亿这件事，反应的是信息流市场的整体繁荣，而对于今日头条来说，其也在面临着那些拥有渠道壁垒、内容壁垒平台的威胁，在内容越来越同质化之下，寻求差异化，寻求不同场景下的内容分发，过度到内容生态，才是值得思索的事情。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">而此时，百度多年积累的优势也正在逐渐显现出来，在百度Q2 净利润同比增长82.9%之后，基于信息流，基于整个内容分发生态，其还有更大的上涨空间。</p><p><br/></p>',0,1,0,0,0,0,150,'http://','','','resource/images/thumb/1513009731405.jpg',''),(5,'深度揭秘：香港电商为何落后于整个时代？',1,200,1,NULL,'原创','2017-12-11 16:52:05','深度揭秘：香港电商为何落后于整个时代？','文/龚进辉　　前不久，乘着热烈庆祝回归祖国 20 周年的春风，香港再度成为舆论关注的焦点。众所周知，香港素有“购物天堂”的美誉，每年吸引成千上万的剁手党不远千里前来买买买，成为拉动香港经济增长的紧张引擎之一。　　固然，香港星罗棋布的零售体系，在让剁手党流连忘返的同时，优先满足土生土长的香港人日常购物需求，网购也是其释放购物欲的重 ...','<p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"/ueditor/jsp/upload/image/20171212/1513009807936062405.jpg\" alt=\"\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　文/龚进辉</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　前不久，乘着热烈庆祝回归祖国 20 周年的春风，香港再度成为舆论关注的焦点。众所周知，香港素有“购物天堂”的美誉，每年吸引成千上万的剁手党不远千里前来买买买，成为拉动香港经济增长的紧张引擎之一。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　固然，香港星罗棋布的零售体系，在让剁手党流连忘返的同时，优先满足土生土长的香港人日常购物需求，网购也是其释放购物欲的重要选项。不外，诡异的是，无论是早期的PC期间照旧风头正劲的移动时代，香港并未诞生有影响力的本土电商平台，市民只能从亚马逊、eBay、天猫等大平台下单，用“被殖民”来形容当下香港电商窘境并不为过。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　反观本地市场，电商不但迅速扎根、开花结果，诞生天猫、京东两大巨头，并且近年来不安于现状的它们开始走出国门，寻找新的增长点。不得不说，香港电商创业落后于整个时代，谈及其为何迟迟不见起色，不少人都能说出市场范围小、履约本钱高等显性缘故原由。当然，文化、创业气氛等深层次原因更发人深思。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　先说香港电商不给力的显性原因，我认为有三点：</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　<strong style=\"margin: 0px; padding: 0px;\">一</strong><strong style=\"margin: 0px; padding: 0px;\">、</strong><strong style=\"margin: 0px; padding: 0px;\">市场规模小。</strong>市场规模是互联网创业者优先考虑的问题，此中用户基数是重要指标之一。截至 2014 年年中，香港人口达 723 万，与内地6. 88 亿网民相比几乎可以忽略不计，甚至不及一个大型城市。即便100%的香港人有网购风俗、购置力强于内地人，市场规模依旧小得可怜，规模上不去导致边际成本无法低落，盈利难度随之上升。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　<strong style=\"margin: 0px; padding: 0px;\">二、电商履约成本高</strong><strong style=\"margin: 0px; padding: 0px;\">。</strong>在香港这个弹丸之地，地皮是公认的稀缺资源，不仅体现在房价高得离谱，贸易地产的快速生长使租金也水涨船高。无论采取哪种电商模式，仓储是不可或缺的重要环节，居高不下的租金成本让电商创业者压力山大，租个几百上千平米的仓库，花销不是小数目。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　同时，别看香港地方小、路程短，高昂的人力支出使配送环节也成为烧钱的主。租金、物流两项支出，使香港整体履约成本不停处于高水平，除非达到可观的规模，否则下降可能性极低。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　<strong style=\"margin: 0px; padding: 0px;\">三、</strong><strong style=\"margin: 0px; padding: 0px;\">线下零售高度发达。</strong>或许你会纳闷，完备的零售体系明明是香港上风所在，为何会成为电商发展的阻力？我一直坚信，优点的过度延伸是缺点。“总有一间在附近”是香港便利店的广告语，7-11、OK便利店、万宁等在香港街头随处可见。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　以7- 11 为例，香港面积约为 1095 平方公里， 900 多家7- 11 分布在各地，开店密度之高令人惊讶。密密麻麻的便利店使香港人极为便利地购买所需商品，通常下楼即可搞定。同时，购物中心琳琅满目的商品满足香港人对购物的所有幻想，几乎每个地铁站附近都有一个中大型购物中心，而且时不时举办促销活动，均在 1 小时生活圈覆盖范围，高效便捷。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　久而久之，香港人对线下零售产生严重的依赖性，在这种商业格局下，电商发展空间大为受限，处境极其尴尬，犹如鸡肋。一方面，电商突破空间限制的优势完全体现不出来；另一方面，电商难以找到合适的切入点，商品多样性、更低价格、服务更贴心等优势均不明显。最终，电商只能作为补充购物手段存在，而无法成为主流，香港人难以养成稳定的网购习惯，为其发展蒙上一层阴影。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　电子产物鲜明地反映出内地与香港商业环境的不同。尽管内地有苏宁、国美等全国性家电连锁企业，用户在天猫、京东等线上平台购买电子产品的积极性依然高涨；反观香港，电子产品足够便宜，市民去实体店购买既快又有保障，网购积极性不高。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　乐视前亚太区执行总裁莫翠天曾分析道，“香港许多线下的销售渠道都很成熟，要让他瞬间切换网上购买你的产品，其实对很多消费者是一个心理障碍，可能会不放心，对你的产品质量、用户体验、售后服务会有很多质疑。”</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　一眼望到头的市场前景、难以负荷的履约成本，再加上线下零售蚕食网购意愿，香港电商发展无缘地利、人和，时间更不站在它这一边，无法做大也就见怪不怪。需要指出的是，凡事事在人为，人类商业史不乏逆境重生的成功案例，只要香港电商创业者愿意打拼，仍存在一丝希望。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　不过，文化变迁、创业热情消退等因素，使香港电商注定难以翻身，这才是其落后于时代的基础原因，怨不得别人。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　<strong style=\"margin: 0px; padding: 0px;\">先说文化变迁。</strong>香港人盛行“拿来主义”。作为东西方文化交汇之地，香港在深受中国传统文化影响之余，也对西方思想、文化具有高度认同感，既没语言障碍又没防火墙干扰，无形中降低“拿来主义”的成本，使亚马逊、eBay本土化之路走得尤为顺畅，香港人得以在第一时间上手，谷歌、YouTube、Gmail等欧美主流互联网产品在香港也拥有极高普及率。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　总之，只要是出色的互联网产品，生活在自由之地的香港人都会热情相待，而不在意到底出自本土还是外国。值得注意的是，香港盛行“拿来主义”与内地抄袭成风形成强烈反差，根本原因在于文化差异。在香港人的思维中，既然有现成产品，拿来直接用即可，不会思考再做个雷同产品；内地人则不遗余力地模仿产品创意，开发适合本土用户使用的产品，并通过适当的商业模式变现。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　<strong style=\"margin: 0px; padding: 0px;\">再说</strong><strong style=\"margin: 0px; padding: 0px;\">香港创业气氛昏暗。</strong>与同为金融重镇的上海遭遇“留不住马云”的尴尬类似，香港互联网创业氛围也不浓，其中不光鲜的电商创业尤为不受待见。除了碍于居高不下的租金和人力成本，社会主流价值观对房地产、金融等热门行业的推许，使电商创业难上加难。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　众所周知，金融、房地产、旅游是香港三大支柱产业，吸引大量优秀人才加盟，拿高薪、生活稳定，久而久之，香港人变得不愿冒险，与台湾年轻人推崇小确幸生活观类似，导致创业者不幸躺枪，无法吸纳精兵强将组建一流团队，风险随之增加，只有零星的“散兵游勇”创业。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　香港有创业意愿的人本来就少，更别提在被边缘化的互联网行业创业，亚洲首富李嘉诚正是“罪魁祸首”。香港是一个成熟经济体，成熟的潜台词是把持，香港1/ 7 的私宅物业、70%的零售市场份额、近一半的港口生意、市场份额最大的电器连锁店和主要电信、电信运营商等都是李家的，李家在香港土地、电信、水电、天然气等多个领域占据一定程度的垄断优势。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　这种垄断优势导致香港创业停留在李嘉诚时代，错过了风云变幻的互联网时代，没有诞生杰出的互联网创业者和一流公司，原本施展空间受限的电商更难有所作为。一步错步步错，就算今天马云手把手指导创业者，或许也难以改变香港电商的惨淡现状。</p><p><br/></p><p><br/></p>',0,1,0,0,0,0,3,'http://','','','resource/images/thumb/1513009795264.jpg',''),(6,'看尚“CAN智慧家庭”引围观 产业链合作共绘客厅经济蓝图',1,500,1,NULL,'原创','2017-12-12 00:30:14','看尚“CAN智慧家庭”引围观 产业链合作共绘客厅经济蓝图',' 7月27日，以“智能引领将来”为主题的2017天翼智能终端交易展览会在广州广交馆正式开幕。据悉，此次博览会为期四天，由中国电信与美国高通公司联手主办，主要议题包罗智能终端制造、大数据、聪明家庭等内容，汇集了来自智能财产生态圈的浩繁企业参展，堪称年度风向标。在2016年天翼博览会上依附“超能智家”混淆云解决方案备受关注的互联网电视品牌看尚， ...','<p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"margin: 0px; padding: 0px;\">7月27日，以“智能引领将来”为主题的2017天翼智能终端交易展览会在广州广交馆正式开幕。据悉，此次博览会为期四天，由中国电信与美国高通公司联手主办，主要议题包罗智能终端制造、大数据、聪明家庭等内容，汇集了来自智能财产生态圈的浩繁企业参展，堪称年度风向标。在2016年天翼博览会上依附“超能智家”混淆云解决方案备受关注的互联网电视品牌看尚，再度携基于全矩阵产业链而打造的智能终端体系，以及更为成熟的智慧家庭解决方案表态，吸引了中国电信市场部总经理王国权等向导以及其它众多参观者的驻足、体验，举世智达总裁付强在展区向莅临领导全面先容了看尚笼罩电视、智能投影、音响、智能家居的多样化产物体系和丰富的应用场景，展示出了看尚对付未来构建智慧家庭、深挖客堂经济的构想蓝图。</span><br/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"/ueditor/jsp/upload/image/20171212/1513009855264077810.jpg\" width=\"554\" height=\"369\" alt=\"C:\\Users\\NaCl\\AppData\\Local\\Microsoft\\Windows\\INetCache\\Content.Word\\DSC_0488.jpg\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">环球智达总裁付强向中国电信市场部总经理王国权介绍“CAN超能智家”产品</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp;“CAN超能智家”吸引众人驻足 看尚智能终端结构获肯定</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">作为海内知名的互联网电视品牌，看尚对大屏智能电视在智能终端产业中的代价有着深刻认识。在今年的博览会上，看尚带来了包括看尚电视“炫看”、“焕看”系列，激光影院S2、看尚声霸S2 Pro、智能家居产品等在内的种种产品，涵盖了智能家居产业链的各个环节。其中看尚巨屏电视X98、激光影院S2等都成为了参观者争相体验的“网红”产品，获得了体验者的一致肯定。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">在看尚展台的参观体验者中，中国电信市场部总经理王国权尤为引人注目。环球智达总裁付强向王国权就看尚的智能终端布局做了详细介绍，并现场演示了看尚围绕用户家庭智能安防、环境监测、智能节能、智能语音等多维度使用场景推出的小看智能摄像机、智能网关、智能插座、智能窗帘、智能传感器等一系列“CAN超能智家”产品。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">付强表现，目前看尚已经推出了一系列智能终端产品，并通过智能家居-混合云解决方案实现了对各种数据和模块化智慧家庭平台的无缝对接，可以全方位的为用户提供高效、便捷的场景化应用服务。同时，看尚依托于CIBN互联网电视，通过智能云平台和终端用户系统，把电视的家庭属性融合到智能家居，以互联网电视为核心整合智能家居，以电视为中心作用控制家庭智能生活。凭借自身互联网电视的上风，看尚将高配置、高音质、高画质整合其中，使看尚电视在智能家居中的作用锦上添花，同时也为用户提供更有价值的生活娱乐新体验。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">对于看尚的智能终端布局，王国权予以高度评价，并表示期待未来看尚可以或许继承深挖客厅经济价值，为消费者带来更多更好的产品。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">全矩阵布局推动产业发展 产业链互助共绘客厅经济蓝图</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">作为国内较早涉足智慧家庭的企业，看尚对于智慧家庭有着独到的理解。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">围绕客厅经济，看尚构建起智能电视机、新一代巨幕智能激光电视、高品质Hifi音响、云盒、智能家居等完整的智能终端体系。同时，看尚不断向产业链的上下游拓展，推出了CAN定制芯片、CAN UI智能电视操作系统，实现了硬件与软件创新的并进，有效服务中国家庭。内容方面，看尚依托CIBN互联网电视，与互联网业界睁开广泛合作，集成了优酷土豆等优质内容资源，形成了业内最全的内容矩阵。看尚还作为业内第一个China DRM尺度落地单位，得到了来自于好莱坞的认可。支持China DRM标准的CAN UI智能电视操作系统不仅可以有效地保护内容的版权安全，也为未来家庭开展电影院线同步播放工作提供了可能。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">目前，看尚已经在智能终端产业的牌照、硬件、操作系统、内容、软件、应用、服务、渠道8个关键环节进行了全矩阵产业链布局，成为业内唯一一个在任何环节都没有缺失，并具有深度和延展度的品牌。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"/ueditor/jsp/upload/image/20171212/1513009855405043465.jpg\" width=\"554\" height=\"369\" alt=\"C:\\Users\\NaCl\\AppData\\Local\\Microsoft\\Windows\\INetCache\\Content.Word\\DSC_0415.jpg\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">看尚智能终端产品亮相天翼博览会</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">付强表示，未来智慧家庭将是一个融合了物联网、远程通信、云计算、人工智能等领域技术的综合产业，客厅经济的深度挖掘离不开产业链各方的努力合作。以中国电信为代表的运营商凭借其在网络建设、云处理、物联网、大数据等方面的优势，能够为智能终端的网络智能化、业务生态化、运营智慧化提供积极助力。看尚也将从多个方面与运营商展开深度合作。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">目前，看尚的智能终端体系已然覆盖到了智能时代家庭生活的方方面面。未来，看尚将继续秉持开放合作的理念，携手运营商以及产业链的上下游企业共同推动智慧家庭产业发展，为用户营造一个集娱乐、便捷、健康、安全等于一体的互联网家。</p><p><br/></p>',0,1,0,0,0,0,9,'http://','','','resource/images/thumb/1513009853718.jpg',''),(7,'谷歌解除Google Photos单相册存最多2000张照片限制',2,200,1,NULL,'原创','2017-12-12 00:31:00','谷歌解除Google Photos单相册存最多2000张照片限制',' 如果你是Gooogle Photos的重度用户，那这对你来说是个好消息：谷歌解除了Google Photos里，单个相册最多只能存 2000 张照片的限定，大概，存储照片张数的上限被大幅度进步。▲图片泉源：SlashGear据外媒SlashGear报道，有用户反馈，当他往Google Photos的单个相册里存储的照片张数远远多于 2000 张时，Google Photos并没有给出错误提示。我们不知道这是不 ...','<p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">如果你是Gooogle Photos的重度用户，那这对你来说是个好消息：谷歌解除了Google Photos里，单个相册最多只能存 2000 张照片的限定，大概，存储照片张数的上限被大幅度进步。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"/ueditor/jsp/upload/image/20171212/1513009908655009568.jpg\" alt=\"\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">▲图片泉源：SlashGear</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">据外媒SlashGear报道，有用户反馈，当他往Google Photos的单个相册里存储的照片张数远远多于 2000 张时，Google Photos并没有给出错误提示。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">我们不知道这是不是Google Photos出了什么题目，谷歌官方也没有针对这一变革颁发任何声明，有用户尝试过之后表现，他已经在Google Photos的一个相册里存了 3300 张照片，什么问题都没有。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">固然谷歌没有给出这样做的缘故原由，不外外媒SlashGear以为，这是谷歌为新的共享功效铺路，由于如果这样做，任何人都可以越发轻松地与他人分享他们的谷歌相册中的内容。</p><p><br/></p>',0,1,0,0,0,0,18,'http://','','','resource/images/thumb/1513009897421.jpg',''),(8,'富士康宣布百亿美元美国建厂特朗普：都是我的功劳',2,300,1,NULL,'原创','2017-12-12 00:32:06','富士康宣布百亿美元美国建厂特朗普：都是我的功劳','当地时间2017年7月26日，美国华盛顿，美国总统特朗普和富士康集团掌门人郭台铭在白宫公布，富士康筹划在威斯康星州建立一家新工场。 东方IC 图富士康赴美投资计划宣布。美国当地时间7月26日，富士康宣布，将在将来4年内在美国威斯康星州投资100亿美元兴建液晶面板厂，将会为威斯康星州创造3000个工作岗亭，并有机会上探至13000人规模。当天，在美国副总统 ...','<p class=\"text\" style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">当地时间2017年7月26日，美国华盛顿，美国总统特朗普和富士康集团掌门人郭台铭在白宫公布，富士康筹划在威斯康星州建立一家新工场。 东方IC 图</p><p class=\"text\" style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">富士康赴美投资计划宣布。</p><p class=\"text\" style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">美国当地时间7月26日，富士康宣布，将在将来4年内在美国威斯康星州投资100亿美元兴建液晶面板厂，将会为威斯康星州创造3000个工作岗亭，并有机会上探至13000人规模。</p><p class=\"text\" style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">当天，在美国副总统迈克﹒彭斯（Mike Pence）、威斯康星州州长斯科特﹒沃克（Scott Walker）及众议院议长保罗﹒莱恩（Paul Ryan）见证下，美国总统特朗普和富士康创始人兼总裁郭台铭在白宫东区举办的仪式上配合宣告这一投资项目。</p><p class=\"text\" style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">富士康称，此举将成为富士康未来数年投资美国的标志性事件开端。今年1月份，富士康董事长郭台铭表现，富士康正思量在美国投资超过70亿美元，创造3万到5万个就业岗位。</p><p class=\"text\" style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">“如果我没有当选的话，他肯定不会投资这100亿美元。”美国总统特朗普将这笔投资视为自己的功劳。在白宫与郭台铭共同出席的仪式上，特朗普称，“这对于美国工人和美国制造业来说是伟大的一天。”</p><p class=\"text\" style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">富士康方面称，由美国总统高级顾问贾瑞德﹒库什纳（Jared Kushner）及总统助理里德﹒科迪什（Reed Cordish）领导的美国白宫创新办公室对此项目推动起到了关键作用。</p><p class=\"text\" style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">特朗普表示：“这对美国制造业、工人以及所有信奉’美国制造’的人来讲，都是一个好日子；以富士康为代表的诸多行业领导者的加持，让世人更加意识到美国工人的无与伦比、美国梦的无限可期，美国的繁荣就在眼前。”</p><p class=\"text\" style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">“这对我们州乃至美国都是一个百年一遇的机会，威斯康星已经准备好了。”沃克州长表示，“我们称之为‘Wisconn Valley’，由于我们相信这一投资给威斯康星州带来的转型升级就像当年硅谷之于旧金山湾区带来的厘革一样；富士康将前瞻高科技制造带到美国，威斯康星州将共同引领这一变革之路，谢谢富士康选择威斯康星州，感谢郭台铭老师对此所做的统统积极。”</p><p class=\"text\" style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">这一全新的液晶面板工厂位于威斯康星州东南部，厂址将于不久后对外公布。</p><p class=\"text\" style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">富士康方面公布的新闻稿称，从新一代电视机到主动驾驶汽车、飞行体系，再到智能教育、娱乐、医疗健康、先辈制造系统，以及办公自动化、互动式新零售、安全生活等，该工厂生产的液晶面板未来将广泛应用于影响消费者日常生活的诸多高科技领域。</p><p class=\"text\" style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">富士康总裁郭台铭表示：“很高兴在美国中心地带建设这样一个最先进的面板工厂，作为在美国诸州投资计划中的首个项目，这一工厂也是我们在美国打造8K+5G生态系统雄伟计划的组成部分。”</p><p class=\"text\" style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">液晶面板重要集中在亚洲，韩国、日本、中国大陆以及中国台湾，相关产业链已经非常成熟。富士康这次前往美国设厂，被外界认为富士康希望在美国市场销售大尺寸的液晶电视。大尺寸液晶面板运输本钱高，本地化生产会带来成本低落。别的，液晶面板厂已经高度自动化，人力成本占比很少，人力成本不是选址考虑的主要因素。</p><p><br/></p>',0,1,0,0,0,0,200,'http://','','','resource/images/thumb/1513009973952.jpg',''),(9,'特朗普：苹果承诺在美建三个大型工厂',3,200,1,NULL,'原创','2017-12-11 16:51:43','特朗普：苹果承诺在美建三个大型工厂','新浪美股讯 北京时间26日早间消息，据国外媒体报道，美国总统特朗普表示，苹果CEO库克承诺在美国建设“三个大型工厂”。　　特朗普在接受《华尔街日报》采访时称：“我与库克通了电话，他向我承诺将建三个大工厂——大、大、大。”特朗普连用了三个“大”字，来强调工厂的规模。　　这是双方就企业税改和商业投资讨论里的一部分。“我说，你懂的，Cook ...','<p><img src=\"/ueditor/jsp/upload/image/20171212/1513010217405043259.jpg\" alt=\"\"/><span class=\"img_descr\" style=\"margin: 0px; padding: 0px;\"></span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　新浪美股讯 北京时间26日早间消息，据国外媒体报道，美国总统特朗普表示，<span style=\"margin: 0px; padding: 0px;\">苹果</span><span style=\"margin: 0px; padding: 0px;\"></span>CEO库克承诺在美国建设“三个大型工厂”。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　特朗普在接受《华尔街日报》采访时称：“我与库克通了电话，他向我承诺将建三个大工厂——大、大、大。”特朗普连用了三个“大”字，来强调工厂的规模。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　这是双方就企业税改和商业投资讨论里的一部分。“我说，你懂的，Cook，除非你在美国建工厂，否则我不会认为我的政府经济上取得了成功。他打电话给我，说他们正推进这件事。”</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　苹果公司拒绝就这一报道置评。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　这并非特朗普首次宣称他与库克商谈苹果在美国建厂事宜。早在竞选期间，他就多次呼吁苹果在美国生产其产品。今年1月他曾表示，库克正考虑在美国建设一座生产厂。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　当然，苹果自己并不生产产品，而是将iPhone及其它电脑产品的生产外包给富士康等代工商。该公司仅在爱尔兰有一些生产业务，并将部分Mac Pro电脑的生产</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　据《华尔街日报》报道，特朗普周二还提及富士康计划在美国建一个大厂，很可能在威斯康星州，有传闻称该厂将生产大尺寸电视面板。《华尔街日报》援引消息人士称，底特律也在富士康建厂考虑范围内，但生产何种产品尚未可知，可能与苹果产品或零部件有关。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　另据《密尔沃基商业日报》报道，富士康最早将于周三宣布在美国建厂计划。</p><p><br/></p><p><br/></p>',0,1,0,0,0,0,35,'http://','','','resource/images/thumb/1513010206764.jpg',''),(11,'三大运营商提前一个月取消长途漫游费账怎么算',1,100,1,NULL,'原创','2017-12-12 19:08:21','三大运营商提前一个月取消长途漫游费账怎么算','27日，中国电信、中国联通、中国移动相继宣布：9月1日起取消周游费！这比之前预计的十月份取消长途漫游费提前了一个月。　　诞生于2G期间的漫游费已难为时代所容　　据了解，手机漫游费是2G时代的产物，诞生于1994年原邮电部发布的《关于加强移动电话机治理和调解移动电话资费标准的通知》，其中要求移动电话需收取每分钟0.6元的自动漫游费。到了2008年， ...','<p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">27日，中国电信、中国联通、中国移动相继宣布：9月1日起取消周游费！这比之前预计的十月份取消长途漫游费提前了一个月。</p><p><img src=\"/ueditor/jsp/upload/image/20171212/1513076947711021559.jpg\" alt=\"\"/><span class=\"img_descr\" style=\"margin: 0px; padding: 0px;\"></span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　<strong style=\"margin: 0px; padding: 0px;\">　诞生于2G期间的漫游费已难为时代所容</strong></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　据了解，手机漫游费是2G时代的产物，诞生于1994年原邮电部发布的《关于加强移动电话机治理和调解移动电话资费标准的通知》，其中要求移动电话需收取每分钟0.6元的自动漫游费。到了2008年，工信部正式规定主叫每分钟0.6元为海内漫游通话费上限。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　不可否认的是，漫游费设立的初衷是为了平衡不同地区生长的差距，为落后地区的通讯建立提供一种额外资金。但随着中国大步迈向5G时代，通信基站建设成熟稳定，仍旧坚挺的漫游费愈难为时代所容。</p><p><img src=\"/ueditor/jsp/upload/image/20171212/1513076947852063491.jpg\" alt=\"\"/><span class=\"img_descr\" style=\"margin: 0px; padding: 0px;\"></span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　现在人们对流量的需求早已多于通话，公众对付流量漫游费的“痛感”要比通话漫游费大得多。对于一些人来说，出差在外如果全国流量用完，而本地流量仍然大量富余，这是最尴尬的。超出套餐之外的流量收费并不便宜，通常一兆就必要两三毛。在智能手机时代，随随便便用几十兆是很快的事变。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　通话方面的长途漫游费可以取消，流量漫游费是不是可以取消？三大运营商提前一个月取消通话方面的长途漫游费，这本账到底该怎么算？</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　<strong style=\"margin: 0px; padding: 0px;\">央视评论</strong></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　无论如何，都得给三大运营商点个赞！毕竟，9月1日起全面取消手机国内长途漫游费，这比预定的时间提前了一个多月，也让人们感受到三大运营商提速降费的诚意。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><strong style=\"margin: 0px; padding: 0px;\">　　三大运营商终将利民功德落到实处</strong></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　事实上，一段时间以来，在工信部的不停督促下，三大运营商不断调整资费项目和标准，把一大批不合时宜的收费项目渐渐降低或取消。而全面取消手机漫游费的说法，始于今年3月全国两会，李克强总理所作的《政府工作报告》中明确写入“全面取消手机国内长途漫游费”，报告行至此处，大会堂内的代表委员掌声雷动。足见，这是百姓期盼已久的事情。从那时起，三大运营商抓紧从技能和管理层面入手，完成相关调整工作，终于让这项让利于民的好事可以提前落到实处。</p><p><img src=\"/ueditor/jsp/upload/image/20171212/1513076947945081255.jpg\" alt=\"\"/><span class=\"img_descr\" style=\"margin: 0px; padding: 0px;\"></span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　<strong style=\"margin: 0px; padding: 0px;\">　漫游费取消多数民众感受不深</strong></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　当然，详细问题具体分析，这一次取消手机漫游费，一些人不会感受到变革。许多消费者的账单里，早就没了漫游费、长途费的字样，取而代之的是套餐费及固定费、套餐外语音通讯费、套餐外短信费以及流量费等。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　更重要的是，在今天如许一个时代，伴随着移动互联网的快速发展，运营商的营收结构和比例在不断发生变化。传统的语音收入逐步式微，而流量成为了收费的新宠，通过流量来通话早就成为了可能。就拿中国移动举例，2017年一季度，中国移动实现营收1840亿元，手机上网流量比上年同期增长101%，总通话分钟数较上年同期下降7.0%。其他两家运营商的情况也大抵云云。在这样的情况下，流量费给消费者带来的痛感更强。运营商选择提前取消长途漫游费固然是向消费者让利，其着实很多人看来更像是送了一个顺水人情。横竖这块业务已经式微，在国外很多地方也早就取消长途漫游费。</p><p><img src=\"/ueditor/jsp/upload/image/20171212/1513076948024045311.jpg\" alt=\"\"/><span class=\"img_descr\" style=\"margin: 0px; padding: 0px;\"></span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　<strong style=\"margin: 0px; padding: 0px;\">提速降费成推动社会创新发展必要手段</strong></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　当前，移动互联网已经极大地改变了我们的生活方式。用一部手机，我们就能方便地逛商场、进便利店，甚至买煎饼果子。近一段时间，依托于移动互联网技术出现的无人超市已经让我们看到了又一个行业的巨大前景。依托于移动互联网的创新仍有非常大的潜力，但各种互联网应用软件在使用的时候都不可避免地需要斲丧流量，整个社会对流量消耗的需求越来越大。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　从这个角度讲，电信运营商给社会提供的服务，同样具有基础性和协同性。提速降费这本账不是运营商的小账，而关乎国家经济发展、技术创新以及新业态发展的大账。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　最后，从本质上讲，在一国之内对费用进行地区分别的架构并不符合互联网产业的逻辑。研究表明，从技术的角度讲，国内漫游的本钱并不高。而之所以会产生成本，很大程度上就是各个以区域来划分的分公司的利益。当下，三大电信巨头受到互联网企业的冲击很大，拥抱互联网头脑是适应变革的必选之路。因此，破除这样的收费模式本身也是在突破传统的地区支解思维，走向以用户为中心的经营模式。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　因此，无论从哪个角度讲，我们都期待取消通话的长途漫游费只是一个开始。</p><p><br/></p>',0,1,0,0,0,0,0,'http://','','','resource/images/thumb/1513076932461.jpg',''),(12,'马云公益基金会出资1000万美元成立非洲青年创业基金',3,200,1,NULL,'原创','2017-12-12 19:33:03','马云公益基金会出资1000万美元成立非洲青年创业基金','美股行情中心：独家提供全美股行业板块、盘前盘后、ETF、权证实时行情 当地时间7月21日晚，在Youth Connket非洲青年峰会后与媒体见面。　　马云公益基金会出资1000万美元建立非洲青年创业基金 资助非洲青年实现空想　　中国日报网7月22日电 “1000万美元的非洲青年创业基金，资金完全来自我小我私家的基金”，当地时间7月21日晚，在Youth Connket非洲青年 ...','<p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; line-height: 30px;\"><a href=\"https://zyuanoss.oss-cn-beijing.aliyuncs.com/data/attachment/portal/201707/27/091105e58thifruzry218z.jpg\" target=\"_blank\" style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); text-decoration: none; transition: color 0.15s ease; outline: none; cursor: pointer;\"><img src=\"/ueditor/jsp/upload/image/20171212/1513078431055011000.jpg\"/></a></p><p><span class=\"img_descr\" style=\"margin: 0px; padding: 0px;\">当地时间7月21日晚，在Youth Connket非洲青年峰会后与媒体见面。</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　<span style=\"margin: 0px; padding: 0px; font-family: KaiTi_GB2312, KaiTi;\">马云公益基金会出资1000万美元建立非洲青年创业基金 资助非洲青年实现空想</span></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　中国日报网7月22日电 “1000万美元的非洲青年创业基金，资金完全来自我小我私家的基金”，当地时间7月21日晚，在Youth Connket非洲青年峰会后的媒体见面会上，马云详解了成立非洲青年创业基金的资金来源和出发点。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　马云表现，这个创意来自这几天的非洲之行，看到这么多的创业者，有很好的创意，本身应该去支持他们。“我希望表现我对非洲的信心，以是在马云公益基金会成立一个1000万美元的非洲青年创业者专项基金，来帮助非洲的年轻人实现他们的梦想和理念。”</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　马云同时也透露，因为是自己的钱，所以并不需要得到谁的批准。但对于非洲青年创业基金来说，很重要的是要建立一个团队，所以自己很快就组建队伍，来支持基金在非洲的运作。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　“我想这是为非洲专门量身打造的，支持非洲，支持非洲创业者，通过互联网支持非洲的发展。”马云表示。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　马云还对一起参加媒体见面会的联合国贸发会议秘书长基图伊表示，联合国贸发会议如果有资源，欢迎一起合作，看哪些是值得去支持的。支持非洲青年的不单是钱，<span style=\"margin: 0px; padding: 0px;\">阿里巴巴</span><span style=\"margin: 0px; padding: 0px;\"></span>有技能、有资源，也可以配合马云公益基金会一起，提供一些技术增援，但是资金完满是自己个人出资。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　负担出资1000万美元的马云公益基金会由马云个人在2014年提倡并捐赠成立。现在推进中的公益项目主要有“马云墟落西席筹划”、“马云乡村校长计划”，以及全新打造的“教诲网络平台”。马云乡村教师奖每年出1000万元嘉奖100名良好乡村教师，马云乡村校长计划则投入2亿元表扬潜在的乡村教育家。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　当天，在同卢旺达总统卡加梅对话中，马云还宣布，阿里巴巴将资助200个非洲年轻企业家到杭州学习、与非洲大学和政府合作对学生进行培训、自己联合发起成立的桃花源基金会出资设立非洲保护区巡护员奖励基金。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　马云末了表示，这是一次远超预期的学习之旅。自己会勉励尽可能多的员工、尽可能多的中国人来非洲学习和分享。自己也会返来，会去尽可能多非洲的国度。</p><p><br/></p>',0,1,0,0,0,0,0,'http://','','','resource/images/thumb/1513078411461.jpg',''),(13,'微信封杀淘宝客？多元化发展是淘宝客未来最好出路',1,100,1,NULL,'原创','2017-12-12 19:40:32','微信封杀淘宝客？多元化发展是淘宝客未来最好出路','品牌推广就用这几招，七月运动立减500-1000元 　　为了探求流量，商家们“无所不用其极”，而“淘宝客”自淘宝网兴起之初就担当着紧张的引流角色，直到厥后的淘宝联盟改革，PC端向移动端转型……等一系列外在因素的变化，淘宝客们也经历着各种各样的转型。　　从做网站到做QQ群再到做微信群，日前，微信已经成了淘宝客盈利的重要渠道之一。以是，最近一 ...','<p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><strong style=\"margin: 0px; padding: 0px;\">微信为什么封杀淘宝客?其实阿里也封杀过</strong></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　从淘宝客的角度上来看，把最好的流量资源引入最好的电商平台，并从中谋利赢利，是一件再简朴不过的事。但问题是，流量来自腾讯，赚钱的却是阿里，简单来说，淘宝客们的存在，让原本属于腾讯的流量，为阿里变现提供了最好的桥梁，也即，用腾讯的资源给阿里赚钱，所以腾讯要“封杀”淘宝客，阿里要为淘宝客保驾护航。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　早些时候，在微信中分享淘宝链接也被克制过，后来经过几番反抗，就出现了“淘口令”，一种可以分享在微信群中、又可以在手机淘宝中找到特定商品的“特制”链接，此次“禁止”到此也就告一段落。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　事实上，在面对微信的封杀时，阿里虽然给出了“淘口令”等对策，摆出分分钟要帮助淘宝客们与腾讯对抗到底的样子，但是当年那些做大的淘宝客也是受到过阿里的封杀的，好比蘑菇街与漂亮说的转型，最初以淘宝客网站身份存在的那些导购网站们，由于不再单单只为阿里导流而且已经具有从阿里上分流的能力，所以被封杀后转型。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　所以在为什么封杀淘宝客、以及为何要保全淘宝客这件事上，说到底只是长处相关，有损利益了封杀、有助利益了保驾护航，只是印证了这样一条亘古不变的商业道理罢了。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><strong style=\"margin: 0px; padding: 0px;\">　　此次封杀 对淘宝客对阿里造成了多大的影响</strong></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　</p><center><img src=\"https://zyuan.wang/data/attachment/portal/201707/26/111802m4xeoegk4hnjkyzy.jpeg\" width=\"554\" alt=\"\" height=\"576\"/></center><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　淘宝客存在微信群中的方式，即为了用户提供商家内部优惠券，进而推广了商家产物也赚了佣金的历程，7月中旬腾讯开始在微信对淘宝客举行封杀开始，领券数目就直线下滑，从最初的1300多万将至低于50万。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　同样是刷单一样的存在，淘宝客的存在代价却要远高于“刷单”，因为对付商家来说，淘宝客带来的都是实实在在的真用户，有了这些真实数据，商家可以搞搞薄利多销的活动，即便是耍点本领、只为了刷单“狸猫换太子”，也是躲避了来自阿里官方的“打假”政策的，所以，对于商家来说，淘宝客很重要。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　腾讯对淘宝客的封杀，是一次流量的阻截，阿里在看待这次腾讯封杀淘宝客变乱上，也不比当初应对“腾讯封杀淘宝链接”事件，伤的是商家的外部流量入口，是不是也间接提升了阿里内部流量的价值?虽说是也间接了伤害了阿里的利益，但这次阿里会对淘宝客挺身而出吗?如果相助了，相助的力度又会有多少?究竟，在腾讯的地盘上，连阿里自身也要退一步、把链接变成“淘口令”的啊。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"https://zyuan.wang/data/attachment/portal/201707/26/111802dxfcqd0y686fues8.jpg\" alt=\"2\"/></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><strong style=\"margin: 0px; padding: 0px;\">　　淘宝客们怎么办?只有多元化自救</strong></p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　被阿里封杀的美丽说、蘑菇街转型做了电商平台，因为拥有了独树一帜的流量，而对于现阶段的淘宝客来说，微信群、QQ群等多处曾花费巨大心力做起来的流量，既然要被腾讯封了去阿里的路，不如寻找新出口，比如京东等一切腾讯喜爱的流量出口，或是其他没被腾讯封杀的电商平台。</p><p style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">　　毕竟，流量寄他人篱下，还是要看他人脸色行事，而在这种情况下，寻找多元化流量出口，也就成了自救路上的最佳救命稻草。</p><p><br/></p>',0,1,0,0,0,0,0,'http://','','','resource/images/thumb/1513078862008.jpg',''),(14,'廖炜SEO：如何策划一个SEO网站？写网站策划方案必看！',3,100,1,NULL,'原创','2017-12-12 19:45:15','廖炜SEO：如何策划一个SEO网站？写网站策划方案必看！','1、方向与定位 　　不管是做什么，最重要的就是第一步的方向把握和产品定位，方向解决了我们将来要像哪个方面使劲。这个很好理解。 　　今天来重点说说定位，所谓定位，就是一个产品(我们这里指的就是网站)，定位并不是仅仅两个字就能讲完的，定位包括：用户人群的定位、推广方式渠道的定位、差异化的定位、以及网站的风格定位等等。这其中重中之重的 ...','<p>&lt;img bineoo-storage=&quot;1&quot; src=&quot;https://zyuanoss.oss-cn-beijing.aliyuncs.com/data/attachment/portal/201707/12/152311lq80jefnltftjc07.jpg&quot; border=&quot;0&quot; x&quot;=&quot;&quot; style=&quot;margin: 40px auto; padding: 0px; border: none; vertical-align: middle; max-width: 100%; display: block;&quot;&gt;</p><p><br/><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　1、方向与定位</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　不管是做什么，最重要的就是第一步的方向把握和产品定位，方向解决了我们将来要像哪个方面使劲。这个很好理解。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　今天来重点说说定位，所谓定位，就是一个产品(我们这里指的就是网站)，定位并不是仅仅两个字就能讲完的，定位包括：用户人群的定位、推广方式渠道的定位、差异化的定位、以及网站的风格定位等等。这其中重中之重的是用户人群的定位和差异化的定位，如果这两个定位是正确的那么产品基本上等于成功了一半。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　那至于怎么去定位，在这里推荐给大家一本书，特劳特写的定位，这本书很好的诠释了定位理论。但是也不能全部按照书本上来的来。自己也要研究研究，属于自己的定位方法。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　在这里笔者就分享一下我的一个小技巧，笔者在进行定位的时候会运用到思维导图模式，进行分析，用户群体有多大?商业模式推演!推广渠道举例!同行研究!进行会推论出 差异化卖点，基于用户体验我们要比同行做的更好。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　2、了解同行</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　了解同行是，我们在进行网站策划很重要的一步，所谓知己知彼，方能百战不殆。这一定是有道理的，了解同行主要是了解他的网站的物理架构、URL架构、关键词的布局、现有的收录、排名等情况。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　3、确定核心关键词</span><br/><br/></p><p><img src=\"/ueditor/jsp/upload/image/20171212/1513079146539057364.png\"/></p><p><br/><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　根据定位和了解同行，基本上就能确定核心关键词了。核心关键词的确定至关重要。核心关键词就是整个网站关键词系统的核心，容不得半点错误。通常核心关键词也是难度最大流量最大的关键词。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　这里需要竞争难度和有效流量的考量，有效流量一直是笔者做网络营销的座右铭，我们做网站往往要的并不是有多少人访问有多少人浏览，重要是有多少人可以产生购买，下次还会再来。这才是更加重要的。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　4、对核心关键词进行相关和长尾扩词</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　这个就比较简单了，花时间即可，对核心关键词进行拓展，遇到难度比较大的词就单独拿出来研究研究(研究是否可以做一个单独的栏目进行设计，以及是否还可以下分等等)。这里一般需要借助工具来研究，笔者通常用5118、百度推广关键词规划师、以及百度PC和移动端的下拉和推荐词作为参考。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　5、设计网站</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　设计网站SEO人员需要把网站的架构和草图给画出来，让美工、前端、后端去实现。笔者通常会用PR画图，比较方便。需要注意的是，技术人员往往不是注意SEO等方面的问题，他们只管实现功能，如果有需要特别注意的时候，需要给技术人员标注以及沟通。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　6、产品文档怎么写?</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　产品文档是给技术人员看的文档，通常是草图+word。但是笔者跟很多技术人员沟通后，发现他们并不喜欢看word，通常都是看草图和效果图来实现功能。因为切换麻烦。在这里产品文档建议，直接写在草图的旁边。这样方便技术人员浏览。网站架构和URL结构等说明就单独拎出来写。</span></p><p><br/></p>',0,1,0,0,0,0,0,'http://','','','resource/images/thumb/1513079156274.jpg',''),(15,'关于网站优化的基础之如何做需求',1,200,1,NULL,'原创','2017-12-12 20:25:15','关于网站优化的基础之如何做需求','杭州SEO的刘冰在优化课堂讲关于网站优化过程中是如何做需求的？　　关于网站优化的基础之如何做需求　　1、从下拉框和相关搜索看需求　　百度下拉框和相关搜索是常见的用来分析用户需求的数据，下拉框是最近一周用户的搜索最多的词，相关搜索是最近一个月用户的搜索最多的词。　　（1）下拉框，下拉框中显示的关键词搜索越多越在上面，重要程度依次递 ...','<p><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">杭州SEO的刘冰在优化课堂讲关于网站优化过程中是如何做需求的？</span><br/><br/></p><p><img src=\"/ueditor/jsp/upload/image/20171212/1513081554274048125.jpg\"/></p><p><br/><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　关于网站优化的基础之如何做需求</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　1、从下拉框和相关搜索看需求</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　百度下拉框和相关搜索是常见的用来分析用户需求的数据，下拉框是最近一周用户的搜索最多的词，相关搜索是最近一个月用户的搜索最多的词。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　（1）下拉框，下拉框中显示的关键词搜索越多越在上面，重要程度依次递减。下拉框的关键词形成时间比较短，可以看到最近短时间类用户关注的热点。如果是更新非常快的行业可以抢注内容获得关键词排名。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　（2）相关搜索。相关搜索是最近一个月的数据，从相关搜索可以得出过去一个月用户关心的内容，有经验的SEO结合下拉框数据看得到之后关键词的搜索趋势。企业站更应该关注相关搜索的数据。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　2、从搜索排名看隐藏的需求</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　从搜索主关键词看排名在百度网站，从排名靠前的网站可以知道用户会更加喜欢什么类型的网站。比如图片、贴吧、知道、文库等等，比如搜索养羊，不管是在下拉框里面、相关搜索里面还是在排名里面都看到养羊吧，说明用户有很强烈的讨论需求，那么这样的行业更加适合论坛类型的网站。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　（1）图片，出现百度图片说明用户更关注图片，网站应该有更多清晰的图片展示。比如装修</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　（2）贴吧，出现百度贴吧说明用户有讨论需求，网站可以做一个论坛栏目。比如SEO论坛</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　（3）知道，出现知道说明用户有问答需求，网站可以设置常见问答版块。比如医疗</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　（4）文库，出现文库说明用户有资料下载需求，网站可以提供免费下载链接。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　3、从QQ群和贴吧看需求</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　这种方法就是深入用户中心，去看他们在平时的沟通交流中出现了什么问题，有哪些问题没有被解决，如果在你的网站能很好的解决这么问题，那你就能赢得大量的用户。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　4、创新新需求</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　有时间用户自己也不知道自己需要什么，或者说用户还不知道自己可能需要某一种功能，但是当这一种需求放在用户面前时他会用的非常舒服。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　需求分析，是一种指导性的分析。为什么这么说呢？因为只有知道了用户的需求，你才知道该去做什么、怎么做。就像是设置一个或几个目标，然后开始规划具体的实行方案，从而一个个突破，直到量变引发质变。</span></p><p><br/></p>',0,1,0,0,0,0,0,'http://','','','resource/images/thumb/1513081534836.jpg',''),(16,'做网站SEO与移动端页面SEO不得不知道的几个细节',2,150,1,NULL,'原创','2017-12-12 20:37:26','做网站SEO与移动端页面SEO不得不知道的几个细节','SEO是网络营销的基础，有搜索框的地方都离不开SEO，都存在排名。尤其是随着互联网的高速发展，SEO已是占据领导的地位的推广服务，它是用户们最有兴趣搜索的，也是最具潜在商业价值的服务手段。　　图来自于互联网　　想拥有一个好的排名，就必须不断的做网站优化，但我们与别人都做SEO优化，怎样才能让自己不处于劣势呢?就必须要从细节上找问题，会重 ...','<p><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">SEO是网络营销的基础，有搜索框的地方都离不开SEO，都存在排名。尤其是随着互联网的高速发展，SEO已是占据领导的地位的推广服务，它是用户们最有兴趣搜索的，也是最具潜在商业价值的服务手段。</span><br/><br/></p><p><img src=\"/ueditor/jsp/upload/image/20171212/1513082287242029867.png\"/></p><p><br/><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　图来自于互联网</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　想拥有一个好的排名，就必须不断的做网站优化，但我们与别人都做SEO优化，怎样才能让自己不处于劣势呢?就必须要从细节上找问题，会重视细节的人，这时才更容易的获得成功。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\">网站SEO的几个小细节：</strong><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　1、网站的301和404这个是必须做的事情，404的页面最后设置为导航页面，这样有益于百度蜘蛛的抓取!</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　2、网站快照的更新时间是根据网站历史更新频率和内容质量来决定的，更新频率越快，蜘蛛抓取就越频繁。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　3、所有上传的图片都应该加上ALT属性，并且ALT属性必须符合图片本身的内容，同时也要包含1-2个关键词。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　4、不要使用网页重定向以及框架，在网页中使用Frame(框架结构)也不利于网站收录，因为百毒蜘蛛穿透力不是很强。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　5、当网站内容多的时候，爬虫很难做到每篇都能抓去到，这个时候就要设置站点地图了，这样就会把内容一一列出来，有利于SEO优化。</span><br/><br/></p><p><img src=\"/ueditor/jsp/upload/image/20171212/1513082287430092499.png\"/></p><p><br/><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　图来自互联网</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\">移动端页面的SEO又该注意什么?</strong><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　1、IOS系统下的尺寸及分辨率：</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　iPhone界面尺寸：320x480、640x960、640x1136</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　iPad界面尺寸：1024x768、2048x1536</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　2、Android系统下的尺寸及分辨率：</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　Android界面尺寸：480x800、720x1280、1080x1920...</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　3、很多手机，不如iPhone已经不支持Flash了，一些比较复杂的JS效果也可能导致无法显示，所以，你想要被搜索引擎读取的内容和链接尽量不要放在JS或Flash中，很多情况下，就算用户手机能显示，搜索引擎也不一定能解析出来。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　4、移动网站的APP界面一般由四个元素组成，分别是：状态栏、导航栏、主菜单栏以及中间的内容区域。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　5、独立URL的移动站需要先做好自适配，当手机用户访问PC版时，会自动转向到移动版URL，而PC用户由于某种原因访问了移动URL，自动转向到PC版URL，这个转向百度建议用301，Google用301、302都可以。</span><br/><br/></p><p><img src=\"/ueditor/jsp/upload/image/20171212/1513082287539041929.png\"/></p><p><br/><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　SEO成功金字塔模型图</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　在这个互联网竞争的大环境中，你比竞争对手多做一点点，有可能效果就是差之毫厘，失之千里!SEO从业者需要不断地学习，因为这是一项需要思考的工作，而工作的依托者就是搜索引擎。</span></p><p><br/></p>',0,1,0,0,0,0,0,'http://','','','resource/images/thumb/1513082285430.png',''),(17,'作为UE设计师，该如何合理地进行需求分析？',3,100,1,NULL,'原创','2017-12-12 23:21:58','作为UE设计师，该如何合理地进行需求分析？','实际上，\"需求分析\"这是个老生常谈的话题了，道理大家都懂，但是具体如何落实到工作中又是另当别论了。　　UE设计一般指游戏设计或游戏相关设计，还有网站的ue设计，其实就是User Experience的缩写。用户体验（user experience）是指用户访问一个网站或者使用一个产品时的全部体验。他们的印象和感觉，是否成功，是否享受，是否还想再来/使用。他们能 ...','<p><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">实际上，&quot;需求分析&quot;这是个老生常谈的话题了，道理大家都懂，但是具体如何落实到工作中又是另当别论了。</span><br/><br/></p><p><img src=\"/ueditor/jsp/upload/image/20171212/1513082742930061585.jpg\"/></p><p><br/><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　UE设计一般指游戏设计或游戏相关设计，还有网站的ue设计，其实就是User Experience的缩写。用户体验（user experience）是指用户访问一个网站或者使用一个产品时的全部体验。他们的印象和感觉，是否成功，是否享受，是否还想再来/使用。他们能够忍受的问题，疑惑和BUG的程度。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　(资料来源：百度百科)</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　UE设计师作为产品生命中全流程贯穿的角色，接需求进行优化或者策划可以说是十分正常的。很多新人设计师在接到需求后便是直接开干，这是十分不正确的，因为缺少了分析的过程。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　实际上，&quot;需求分析&quot;这是个老生常谈的话题了，许多的专业文章书籍、大咖干货文都会让读者切记分析。讲真，道理咱们都懂，那么应该怎么做呢？接下来，我便从我的工作经验总结以及专业知识储备，来说说作为一个UE设计师该如何合理地进行需求分析。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\">一、合理地归类你接收的需求</strong><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　上面我提到UE设计师是贯穿全流程的一个职能，因此，这也决定了需求来源的多元性。在急着开始需求分析之前，你首先更应该做好合理地归类需求来源。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　从业务的维度上来说，这有助于你对业务需求方意图的揣摩，最终明确需求的业务目标；从工作的维度上来说，这有助于你培养工作方式的条理性，你可以看到许多职场上的成功人士无不是有条有理地进行工作的，工作的条理性是在职业生涯往上攀登过程中不可或缺的品质，所谓&quot;运筹帷幄之中，决胜千里之外&quot;便是这个道理。</span><br/><br/></p><p><img src=\"/ueditor/jsp/upload/image/20171212/1513082743024055119.png\"/></p><p><br/><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　图片来源于作者的电脑</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　就我个人而言，我通常习惯将需求的来源划分为四个源头，包括：产品方、运营方、战略方以及特殊事务。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　产品方与运营方顾名思义，就是指从产品经理与运营同事来的需求，而战略方是指高层领导直接下达的一些重要的需求，比如对产品的顶层重设计。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　至于特殊事务，这是因人而异的，由于公司组织架构上的特殊或者你的个人能力比较广泛，你有可能会承担UE设计外的一些工作，比如我的特殊事务是用户研究，具体视个人情况而定。归类的形式可以由你自己制定，只要有&quot;归类&quot;的意识即可。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　在合理地归类好你接收的需求之后，接下来便可以真正开始你的分析了吗？还不够，为了让你后续的分析更有方向性，你接下来还要做一个十分重要的前提工作，那就是明确两个诉求，</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\">需求的业务诉求</strong><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\">目标用户的用户诉求</strong><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\">二、明确需求的业务诉求</strong><br/><br/></p><p><img src=\"/ueditor/jsp/upload/image/20171212/1513082743070049922.png\"/></p><p><br/><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　图片来源于网络加工</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\">明确需求的业务诉求就是明确好所接收的需求最终希望达成的业务效果。</strong><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　比如运营同事给你一个包含着交互场景的运营活动策划需求，你便需要明确该运营活动是属于拉新的活动、亦或是属于促活的活动等。具体怎么能更好地明确需求的业务诉求，根据我的经验，最有效的途径有两个，那就是</span><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\">&quot;有效沟通&quot;和&quot;移情理解&quot;</strong><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\">&quot;有效沟通&quot;指的就是跟业务方保持联系。</strong><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　若业务方已有清晰的业务诉求，通过有效沟通我们就能马上明确好业务诉求；若某些情况就连业务方对于自己的业务诉求都不是十分清晰的，我们就需要通过沟通从他们的身上尽可能挖掘出有效的业务信息。比如需求从哪来、需求的背景等等，这时候有效的业务信息对于设计师而言就是得出诉求的最好线索。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\">移情理解&quot;指的是作为UE设计师需要站在业务方的角度来看待问题，了解需求背后的本质层。</strong><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　通俗点来说，就是换位思考，把自己的角色转换为业务方的角色，再去进行业务诉求的探究，比如一个运营活动对于业务方来说，是为了解决业务上什么问题或者达成什么目标，可能是最近新用户增长缓慢，所以运营人员需要做一个活动进行拉新获客，他们需求的核心业务诉求便是拉新并且明显体现在数据上。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\">三、明确目标用户的用户诉求</strong><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　明确目标用户的用户诉求这一步骤，从字面上很明显就能看出，是由两个部分组成的，分别是目标用户与用户诉求。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\">第一部分目标用户是明确用户诉求的前提</strong><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　想要洞察你的用户需要什么，你首先要做的是知道你服务的用户是谁。如何定位你的目标用户？首先要明确的是，在这里说的定位目标用户是基于你的需求业务诉求并行的。因此，业务诉求很大程度上可以帮助我们快速界定我们的目标用户，比如一个拉新运营活动的交互策划，你的目标用户很明显就是新用户，继续细分可能还会包括沉默的游客用户、潜在的种子用户等等。除此之外，作为设计师的你也必须了解平台的战略目标、核心服务人群以及平台的愿景等等，这些信息都会很好地帮助你通过业务诉求来界定你的目标用户。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\">第二部分用户诉求是一个十分艰难的过程</strong><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　明确业务诉求时你面向分析的业务方是对象，而当你在明确用户诉求时，面向的分析对象则是一个群体。明确用户用户诉求的过程，很多专业书籍会使用&quot;洞察&quot;来定义，就我个人而言，我认为这更像是一个&quot;挖掘&quot;的过程。通过对专业知识与实际工作进行结合，我总结了两个在挖掘过程中十分有效的方法，分别是&quot;场景分析&quot;与&quot;表里分析&quot;。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　场景分析即通过为用户设定场景故事的方式来模拟出用户的行为、态度以及心理感受等等，其中场景故事包含用户特征、任务流程与场景环境三个主要元素。比如说一个互联网金融平台的借贷功能，我们就可以这样去设定，</span><br/><br/></p><p><img src=\"/ueditor/jsp/upload/image/20171212/1513082743180054090.png\"/></p><p><br/><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　场景分析卡-原创</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　场景模拟分析的过程中，会涉及许多心理学知识、产品知识等等的使用，在这里我便不展开细说了。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　表里分析顾名思义即对用户诉求进行表层诉求与里层诉求的分析，某些专业书籍上也会称为&quot;表层需求&quot;与&quot;隐含需求&quot;。比如汽车大王福特说的那句经典&quot;</span><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\">当你问用户他们想要什么的时候，他们总会告诉你他们想要一匹更快的马，他们不会告诉你他们想要的是一辆汽车</strong><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">&quot;，就是对表里分析运用的一个经典例子。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\">四、分析总结需求目标，并给你的设计做导向</strong><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　通过对上诉两个诉求的确认，我们可以最终对我们的需求目标进行分析总结，分析的方法包括有前面提到过的场景分析法、数据分析法与理论分析法等等。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　数据分析法需要我们对定量数据获取的方法有所掌握，比如问卷调查；</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　而理论分析法需要我们对专业知识的掌握，比如复杂性守恒定律等；</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　最终得出的需求目标需要包含以下三要素，</span><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\">正确业务目标、合理用户目标和合理实现策略</strong><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">，三要素中的目标实际是对诉求的颗粒度缩小精准，比如拉新的业务诉求的业务目标为拉新1000名新用户、有效用户转化率为50%、转发量为1000人以上等等。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　详细如何精细化可以参考著名的&quot;SMART&quot;原则。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　S代表具体(Specific)，指绩效考核要切中特定的工作指标，不能笼统；</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　M代表可度量(Measurable)，指绩效指标是数量化或者行为化的，验证这些绩效指标的数据或者信息是可以获得的；</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　A代表可实现(Attainable)，指绩效指标在付出努力的情况下可以实现，避免设立过高或过低的目标；</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　R代表相关性(Relevant），指绩效指标是与工作的其它目标是相关联的；绩效指标是与本职工作相关联的；</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　T代表有时限(Time-bound)，注重完成绩效指标的特定期限。(资料来源：百度百科)</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　需求目标总结出来后，最后一步就是将其导向你的设计过程中，包括设计目标确立的过程、设计进行的过程与设计验证的过程。以目标为导向进行设计将会让你的设计对用户更有效、更易用、更有价值。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\">五、结语</strong><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　这篇文章我花了十分长的时间来总结，写完我发现实际上要将一件你日常都在做的事情有条理地说出来，并且形成方法论，着实是一件十分困难的事情。因此，我耗费的大量的时间来总结并且完善我的工作方式。但是，在这次总结中，我也收获了很多，比如学会更稳定地调整自己的工作方式、更专业化地去看待用户体验这件事情。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　有人看完我的这篇文章，可能会疑惑&quot;</span><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\">为什么从头到尾都好像很少提到从用户角度出发等字眼，UE不是应该关注用户体验吗，为什么那么紧张业务呢？</strong><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">&quot;，其实我从来都没有脱离过用户，相反我与其更接近了。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　作为UED我们应该要学会权衡，不要做一个矫情的用户代言人，就像我特别欣赏汪方进说的&quot;</span><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\">我们要站在用户的角度去思辨业务，与业务一同成长</strong><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">&quot;。</span><br/><br/></p><p><img src=\"/ueditor/jsp/upload/image/20171212/1513082743242051709.png\"/></p><p><br/><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　需求分析思维模型-原创</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　最后，我将上述所有的分析过程，总结成了一个以流程图形式进行构建的思维模型，希望大家在看了我的文章后，也能尝试对自己工作的某些部分进行总结与反思，进而更好地提高自己。</span></p><p><br/></p>',0,1,0,0,0,0,0,'http://','','','resource/images/thumb/1513082727445.png',''),(18,'网站域名惹大祸，坏的域名会造成百度不收录',2,300,1,NULL,'原创','2017-12-12 21:01:03','网站域名惹大祸，坏的域名会造成百度不收录','有很多北京网站建设的客户询问我，为什么我的网站都上线很久了，可是在百度上就是找不到我的网站呢?百度不收录，对于企业开展网络营销有很大的阻碍，因为百度不收录，就没有排名，没有排名那就说明网站不会在百度上获得流量，也就无法让潜在客户找到你。　　如果网站不被百度收录时，问题可是相当的严重，不仅让网站建设着担心，出资开发网站的企业或 ...','<p><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">有很多北京网站建设的客户询问我，为什么我的网站都上线很久了，可是在百度上就是找不到我的网站呢?百度不收录，对于企业开展网络营销有很大的阻碍，因为百度不收录，就没有排名，没有排名那就说明网站不会在百度上获得流量，也就无法让潜在客户找到你。</span><br/><br/></p><p><img src=\"/ueditor/jsp/upload/image/20171212/1513083710508000507.jpg\"/></p><p><br/><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　如果网站不被百度收录时，问题可是相当的严重，不仅让网站建设着担心，出资开发网站的企业或个人更揪心。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　其实网站上线后不被百度收录的原因有很多的，特别是被百度K过的域名，那是万万不能用的。所以我觉得很有必要来和大家共同交流一下网站建设中如何选择域名问题:</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　一、好的域名能使网站更具有快速被收录和排名的优势;</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　二、好的域名更能让人记忆犹新，过目不忘;</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　三、有问题的域名能让蜘蛛爬行困难，有大问题的域名更让百度不给予收录。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　前两个问题对于百度不收录没有什么影响的，第三天才是百度不收录的关键所，在今天我具体说说第三点：诊断域名是否被搜索引擎K过!</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　当我们确定从域名服务商注册或者从别人手上购买域名的时候，出于优化考虑，所以在购买前必须要判断下这个域名是否被搜索引擎(百度，Google)K过(虽然说被K过的域名还是可以通过一些措施让域名被搜索引擎认可，但是那样子无疑会需要投入一定的时间和精力)</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　一 link指令：</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　百度：link 并不代表一个指令，只是一个普通的关键字。link:www.bjycxf.com 搜索结果是包含这样一个关键字(link:www.bjycxf.com )词组的所有网页，与其他普通关键词的搜索一样。并不是查找反向链接。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　Yahoo：查找的是反向链接，但需要在域名前加 &quot;https://&quot;，如：link:https://www.bjycxf.com 注意：域名加与不加&quot;www&quot;结果是不一样的。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　二 domain指令：</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　百度：查网页内容中包含某关键词。如：domain:www.bjycxf.com 搜索结果是包含关键字词组(www.bjycxf.com )的所有网页。同理，domain:北京seo 指的是包含seo的所有网页，与直接搜索 北京SEO 相似。如果要查找有多少个网页内容中包含你的网址，domain可以派上用场。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　Yahoo：与 site 一样。查找域名下的所有收录网页。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　特别说明：</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　这些指令原理：如果域名被启用过，那么可能还会有在搜索引擎中还未被删除的收录页面以及一些存在我们网站的反链接页面。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　目前对于百度K过的域名还没有好的方法让其恢复百度对此域名的认知，也是说被百度K过的域名想要恢复的可能性几乎为零，所以我们在投入金钱进行北京网站建设时，一定要选择好域名，不然就会铸成无法弥补的错误。</span></p><p><br/></p>',0,1,0,0,0,0,0,'http://','','','resource/images/thumb/1513083697414.jpg',''),(19,'良好的用户分析配上网站内部优化才是王道',2,125,1,NULL,'原创','2017-12-12 23:28:54','良好的用户分析配上网站内部优化才是王道','作为seo职业人员，有很大一部分每天都在不停的发外链写文章，可是排名就是不理想，面对老板的压力如果还是一味的重复上面的操作，最后的结果就是排名停止在原地，无法获得想要的名次。面临的就是被炒鱿鱼的危险。可是我们真的反思过吗?网站排名哪方面起着决定性的作用呢?今天就和大家讲一下，外链的发布要在网站内部优化完善的情况下才会发挥最大效果 ...','<p><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">作为seo职业人员，有很大一部分每天都在不停的发外链写文章，可是排名就是不理想，面对老板的压力如果还是一味的重复上面的操作，最后的结果就是排名停止在原地，无法获得想要的名次。面临的就是被炒鱿鱼的危险。可是我们真的反思过吗?网站排名哪方面起着决定性的作用呢?今天就和大家讲一下，外链的发布要在网站内部优化完善的情况下才会发挥最大效果，内部优化在关键词排名中起着绝对性的作用。内部优化需要我们怎么操作呢?</span><br/><br/></p><p><img src=\"/ueditor/jsp/upload/image/20171212/1513085174039085099.jpg\" alt=\"\"/></p><p><br/><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　网站内部优化的核心就是满足用户的需要，要满足用户的需求，我们就要先了解用户的需求，这样才能有一个良好的结构布局。我们要换位思考，如果你是用户的话你最想要了解的是产品的哪个方面?我们可以利用百度的搜索结果和下拉框相关搜索等方面进行分析用户。最近天气炎热，搜索关键词是&quot;空调&quot;的用户就非常多，我就用这个关键词举个例子。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\">1、百度下拉框和相关搜索分析</strong><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　百度下拉框是最直接体现用户最近一周内频繁搜索的关键词，我们搜索空调这个关键词，下拉框中高频率的关键词有，空调的维修、空调的清洗、空调品牌的排行榜，这说明一部分用户是已经拥有空调的，他们只对空调的维修和清理感兴趣，说明了通过这个关键词来的流量虽然多，但是不精准，10个用户能有2个是为了来买空调才搜索的，这种情况建议不要做这种泛词，这种词不是公司类型的团队是无法做起来的，即使做起来了，流量也不精准，成单率低，而且这种泛词周期性可能会很长，这就是费力不讨好。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　像空调的品牌，空调的排行这种长尾关键词，用户一旦搜索这类词，就是有购买的意向，我们把这类词做上去后，用户虽然比泛词少一些，但是流量精准，搜这些词的用户都是有购买欲望的。所以建议大家一定要选好核心关键词，不要太泛，要有精度和准度，让用户搜索到你就想要买产品才行。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\">2、关键词自然排行和竞争对手分析</strong><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　在搜索空调这个关键词后，我们要看搜索引擎给出的自然排名，不要把竞价算在内，进行分析。搜索空调后自然排名第一的位置是百度百科，这个问题之前我也讲过，为什么自然排名第一的位置是百度百科呢，我们在搜索下拉框中也能分析出这个问题。因为在搜索这个词的用户有绝大多数是了解空调的基本知识和空调维修清理知识，所有百度百科排在了第一的位置。再往下看自然排名第二位置的是家电分类网站，这种网站的权重就是比较高的，网站内部链接数以万计，相关性的知识也非常的丰富，就和淘宝，京东类似。是我们个人无法完成的。这里就不做过多的说明。而排名第三的位置是百度图片，这就补充的说明了百度百科为什么会排在第一位置。因为搜索这个词的用户主要关心的就是空调的图片，也就是空调的样式和空调基本原理等方面的知识。</span><br/><br/></p><p><img src=\"/ueditor/jsp/upload/image/20171212/1513085174086041918.png\" alt=\"\"/></p><p><br/><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　作为我们竞争对手的网站我们竟然在第一页没有看到，第一个基本都是一些大型家电网站。他们也意识到了，单单做这个词如果没有非常大的实力是无法完成的。即使做上去了，流量也不精准，成单率可能会很低。所有我们还是要以前面说过的长尾关键词为核心进行网站的布局。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　做了这么多的用户分析，我们到底如何来布局网站呢?如果想要有最大量的转化，就要以空调的品牌或者空调的排名为核心。用户还对空调的一些知识和图片非常感兴趣，所以整个网站的导航栏要围绕着空调的品牌和空调的百科和空调产品图片进行布局。这是就网站内部布局的核心。在网站我们尽量也不要设置自动弹出对话框，这种对话框对用户的体验度是及其差，如果我遇到这种情况会直接选择离开。说明网站核心布局一定要考虑到用户的体验度，如果连良好体验度都不能保证，那谁还会再进入这个网站呢?</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　上面我们分析了用户需求，和网站核心关键词，根据这两点对网站栏目有一个良好的布局。接下来我们要做的就是要对网站URL进行优化，因为我们都知道，我们网站的URL越短，层级越少，蜘蛛越容易抓取，一个理想的URL层级不要超过3层。其内部不要出现中文，文章页的URL尽量和文章标题相呼应。让用户有种视觉享受，如果你的URL又长，层数有多，在搜索引擎方面被收录的可能性很低，这更别说有个好的排名，在用户的角度来讲，他看到很长的URL，他就会有种不信任的感觉，是不是这个网站有问题?可能就会直接关闭网站，导致跳出率增加，是对网站很不好。内部URL尽量不要用动态URL，以目前搜索引擎抓取动态URL是内有问题的，但是动态URL太长的话还是不利于蜘蛛的抓取。如果站内有动态的URL建议其静态化。一定要记住URL越短越好。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　网站内链也是内部优化的一个重点，一般来说网站获得的内部链接越多，其权重也就最高，链接到的一级分类目录页面，这些页面的权重仅次于，层级越往下，权重也就越低。如果我们想要把一些距离权重低的页面拉高权重，就可以在直接加上这个链接，可以在侧边栏添加都可以起到增加权重的作用。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　今天内部优化只说了一个大框，具体细节操作还会给大家再次分享，大家一定要记住核心点，就是一个高质量网站内部优化的前提是需要有精准的用户分析!</span></p><p><br/></p><p><br/></p>',0,1,0,0,0,0,0,'http://','','','resource/images/thumb/1513085160945.jpg',''),(20,'网站细节优化、图片收录有什么样的标准？',1,500,1,NULL,'原创','2017-12-11 18:44:50','网站细节优化、图片收录有什么样的标准？','我们在上次讨论中，很多人说到关于网站图片百度收录排序问题。为此，我们让负责seo优化的同事进行讲解，为大家讲述了百度在图片搜索中的排序基本原则，后续还讲述了百度站长平台后续会继续开放百度图片搜索功能，我们继续为大家提供最新相关的资源信息，请各位站长持续关注百度站长公告的内容。那么，我们今天就来讲讲百度图片搜索希望收录和展现的图 ...','<p><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">我们在上次讨论中，很多人说到关于网站图片百度收录排序问题。为此，我们让负责seo优化的同事进行讲解，为大家讲述了百度在图片搜索中的排序基本原则，后续还讲述了百度站长平台后续会继续开放百度图片搜索功能，我们继续为大家提供最新相关的资源信息，请各位站长持续关注百度站长公告的内容。那么，我们今天就来讲讲百度图片搜索希望收录和展现的图片应具有以下三个维度的特点：</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\"><br/></strong></p><p><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"/ueditor/jsp/upload/image/20171212/1513085237195067577.jpg\"/></strong></p><p><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\"><br/><br/>　　</strong><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\">一、图片在网页中的成分</strong><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　1、网站中所有的图片在网页内从事的经营方法和目的一致，信息相关展示与图片一致。那么，这种情况百度搜索会认为你提供的信息具备粘性和相关性，该页面上的图片和内容更有可信度。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　2、在网站中内容周边建立更精确精准的图片，针对图片做相关描述，包括上下文描述，图片的说明，alt属性、图片title，以及图片anchor等相关工作。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　3、网站页面、栏目、页面必须没有访问权限，这是百度搜索规则之一。假如你的栏目或者页面设置权限了，蜘蛛是没办法输入账户和密码工作的，百度肯定抓取不到你的信息，还会给用户在浏览网页的过程中造成非常不好的体验。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　4、图片的链接不能写在JS里，更不要使用一些异步加载的方式来进行展现。现在对于百度来说JS的解析成功率等待提升。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\">二、图片在搜索的角色</strong><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　1、我们都知道在seo优化过程中，网页的加载速度是蜘蛛重中考量之一，如果我们网页存放的服务器不够稳定，就会造成打开缓慢。为了避免这种情况，我们必须选择稳定的服务器商，必须保证网页很流畅的访问。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　2、由于目前的蜘蛛程序，不管是网页搜索还是图片搜索都是公用的Baiduspider，如果想使网页中的图片有较好的表现，让蜘蛛来读取。就需要你网站对Baiduspider充分的友好，来百度百度对你站点的解析和收率。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　3、网页会在百度图片搜索程序中的搜索体系中进行评价打分，一般来讲，如果你的网页搜索系统拥有很高的可信度，不存在恶意作弊，恶意广告，恶意捆绑之类的行为或过度的SEO行为。图片在搜索中也会得到很好的表现。</span><br/><br/></p><p><br/></p><p><img src=\"/ueditor/jsp/upload/image/20171212/1513085237289030294.png\"/></p><p><br/><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);\">三、图片自身的表现</strong><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　1、图片清晰展示：为什么要说图片清晰呢?清晰的图片在百度搜索排序时或在其他条件相当的情况下，可以到有限的展示权。同时更利于用户浏览体验。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　2、图片尺寸匹配：一张图片不仅仅需要清晰度。还需要相对应的的尺寸。具体的大小尺寸可根据所展示内容进行相关匹配。值得我们注意的是，图片并不是越大越好，尺寸小了也不会受到某种歧视。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　3、图片突出主要内容：在图片展示中，图中突出文字内容，会增加视觉效果。还能让用户一眼就能明白你图所表达的是什么。有必要时，可以增加水印等操作，水印应该添加在边角的位置，不能对图片主体造成遮挡。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　4、做好原创图片：原创图片意味着该图片是稀缺的图片，在百度图片资源库中，没有相同的图片展示。这么一来，你提供的稀缺图片就很容易受到百度搜索的青睐。在此，值得大家注意的是，如果你提供的图片是原创图，那么，是需要你在图片边上配上文字说明。千万别忘了。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　其实在seo环节，图片展示在seo中也占据了很大的重要性。要做好图片搜索真的非常简单，与内容相关性，处理好图片的清晰度，做好原创图片，在图片中增加有价值的文字描述。这些就是图片优化的细节，今天的话题我就讲到这里。</span></p><p><br/></p><p><br/></p><p><br/></p>',0,1,0,0,0,0,0,'http://','','','resource/images/thumb/1513085224930.jpg',''),(21,'互联网时代，企业如何建一个会自动挣钱的网站',3,200,1,NULL,'原创','2017-12-12 23:18:42','互联网时代，企业如何建一个会自动挣钱的网站','互联网时代，企业如何建一个会自动挣钱的网站？PC、手机、微信几大平台的流量，我们如何一手全抓呢？不用人工客服参与，网站自动引导达成产品销售和招商加盟，这样的网站你想拥有吗？　　这种网站就是营销型网站，它就像一个业务员一样，了解客户的痛点和需求，善于说服直到，能抓住客户的注意力，有效地传达品牌和产品的优势，不用人工干预，顺利促使 ...','<p><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">互联网时代，企业如何建一个会自动挣钱的网站？PC、手机、微信几大平台的流量，我们如何一手全抓呢？不用人工客服参与，网站自动引导达成产品销售和招商加盟，这样的网站你想拥有吗？</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　这种网站就是营销型网站，它就像一个业务员一样，了解客户的痛点和需求，善于说服直到，能抓住客户的注意力，有效地传达品牌和产品的优势，不用人工干预，顺利促使来访客户留下销售线索或者直接下订单。</span><br/><br/></p><p><img src=\"/ueditor/jsp/upload/image/20171212/1513085331258048721.jpg\"/></p><p><br/><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　目前，市场的消费主力已经是80、 90 后，这一代人呼吸着互联网长大的，习惯于依赖互联网来获取信息和消费，要俘虏他们，就必须要用他们的方式去触达他们。那么我们就需要一个会自动销售的网站，选择一个好的网站服务商，非常至关重要。云指建站，专注于帮助企业搭建成本低、流量大、见效快、咨询量多、推广易的全网营销平台，凭借着 12 年的互联网服务经验，让你的网站天生具有成交力。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　1、覆盖所有平台流量，一个客户都不放过</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　云指建站采用了国际先进的H5 技术，建一个网站就可以同时适配电脑、手机、平板、微信使用，再也不用像以前那样电脑、手机站分开搭建了，省钱省心省力。一个网站覆盖了所有平台用户，不管你的客户用任何一种方式上网查找信息，都能找到你，爱上你，把你的产品带回家。</span><br/><br/></p><p><img src=\"/ueditor/jsp/upload/image/20171212/1513085331367079207.jpg\"/></p><p><br/><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　2、强大的视觉冲击力，第一眼打动客户</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　会挣钱的网站，往往第一眼就能打动客户的心。云指建站利用图文特效和视频特效，结合时代主流的风格样式，摒弃过去单一死板的展示方式，让产品的展示更加凸显卖点更具有销售力，让企业的优势展示更彰显企业差异化竞争力，凸显品牌实力，让网站达到主动营销的效果，最终促成成交。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　3、超强的推广运营系统，流量不再是难题</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　网站流量一直是网站运营最大的难题。云指建站全新的&quot;企业+分销员+粉丝&quot;营销模式，结合微红包、好友助力、大转盘等数十种互动营销活动，轻松将成千上万的粉丝变成产品的分销员，让你的产品和品牌迅速推广开来，让源源不断客户发现你，瞬间引爆销量。</span><br/><br/></p><p><img src=\"/ueditor/jsp/upload/image/20171212/1513085331477051452.jpg\"/></p><p><br/><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　4、实时互动沟通，转化率高</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　微客服、QQ客服等完善的在线客服系统，与客户实时互动沟通，快速提高询盘率和成交转化率，把流量变销量。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, STHeiti, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">　　一个会营销的网站，是每一个企业在移动互联网时代必须具备的营销利器。选择好的建站服务商能事半功倍，云指建站， 12 年的技术沉淀， 50 万用户的选择，目前所有版本全部提供免费试用，完全零风险，注册成为会员即能领取试用，还能领取 3000 元代金券</span></p><p><br/></p><p><br/></p>',0,1,0,0,0,0,0,'http://','','','resource/images/thumb/1513085319789.jpg','');

