/*Table structure for table `user_list` */

DROP TABLE IF EXISTS `user_list`;

CREATE TABLE `user_list` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(20) NOT NULL,
  `password` varchar(20) NOT NULL,
  `nick` varchar(20) DEFAULT NULL,
  `money` float DEFAULT '0',
  `points` int(11) DEFAULT '0',
  `avail` float DEFAULT '0',
  `groupId` int(11) DEFAULT '1',
  `vipExp` datetime DEFAULT NULL,
  `vipRv` float DEFAULT '0',
  `phone` varchar(11) DEFAULT NULL,
  `qq` varchar(20) DEFAULT NULL,
  `email` varchar(50) DEFAULT NULL,
  `realName` varchar(20) DEFAULT NULL,
  `bankName` varchar(20) DEFAULT NULL,
  `bankCard` varchar(19) DEFAULT NULL,
  `bankAddress` varchar(200) DEFAULT NULL,
  `regDate` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `loginDate` datetime DEFAULT NULL,
  `ip` varchar(50) DEFAULT NULL,
  `islogin` int(11) DEFAULT '0',
  `status` int(11) DEFAULT '0',
  `picPath` varchar(200) DEFAULT 'images/head/default.jpg',
  PRIMARY KEY (`id`),
  KEY `FK_user_list` (`groupId`),
  CONSTRAINT `FK_user_list` FOREIGN KEY (`groupId`) REFERENCES `user_group` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=utf8;

